@-moz-document domain("tele.smotr.online") {
	/* Темная тема */
body {
    color: #E0E0E0;
    background-color: #181B1F;
}

	/* Отступ сверху */
.advert {
    padding: 1px;
}

	/* Логотип и название канала */
.header {
    display: table;
    margin: 15px 0 0px;
    zoom: 30%;
    margin-left: auto;
    margin-right: auto;
}
.header img {
    display: none!important;
}
	/* Шрифт названия канала */
h1 {
    padding: 0;
    vertical-align: middle;
    font-size: 300%;
    font-family: sans-serif;
    color: #f7f7f7;
    text-transform: uppercase;
    display: centre;
}

	/* Убрать большой отступ */
#vid_vpaut_div {
    display: none!important;
}

	/* Размер плеера */
#player {
    zoom: 180%;
}

	/* Выровнять плеер */
.box {
    width: 1100px;
    margin: 0 auto;
}

	/* Остальной текст */
a, .now, .playing {
    color: #f7f7f7;
    text-decoration: none;
    font-family: sans-serif;
}

h2, .description {
    font-family: sans-serif;
}

.description {
    text-shadow: 1px 1px 2px black;
    font-family: sans-serif;
    color: #FFFFFF;
}

label, .info {
    font-family: sans-serif;
    color: #E0E0E0;
}

input:checked+label {
    background: #fafade;
}

	/* Прогресс */
.iner {
    border-right: 2px solid #ccc;
    background-color: #ad1919;
}
.out {
    width: 98%;
    border: 2px solid #ccc;
}

	/* Прогресс СЕЙЧАС*/
.small {
    font-size: 100%;
    color: #FFFFFF!important;
    font-family: sans-serif;
    text-shadow: 1px 1px 2px black;
}
}

