.quest-child-item span.quest-child-status {
      border: solid 2px #e5e2e2!important;
}
g[data-original-title] > text {
    fill: white!important;
}
.container-fullsize .container-inner-item .overflowable-item {
    overflow-anchor: none;
	overflow-x: hidden;
}

g[data-original-title] > rect {
  stroke-width: 0.6!important;
  stroke-linecap: butt!important;
  stroke-dasharray: 0!important;
}
g[data-original-title='0h00'] > rect, g[data-original-title] > rect[fill='#fafafa'] {
    fill: #2f2c2ecc!important;
	stroke: transparent!important;
}
g[data-original-title='Wrong data sent. Contact your pedagogic team.'] > rect {
	fill: #d92222f0;
    stroke: transparent;
}
g[data-original-title] > rect {
	stroke: transparent;
}
g > g > rect {
    fill: transparent!important;
    stroke: transparent;
}
