/* ==UserStyle==
@name           Loadingalternativet 1.3
@namespace      github.com/openstyles/stylus
@version        1.3.0
@description    Ett tema för Loading.se
@author         Kenny
==/UserStyle== */
@-moz-document url-prefix("https://loading.se") {

/*Generellt*/

    .App {
        background: #000000;
        color: #cccccc;
    }

    .custom-loadingkalendern-note {
        background: #fd2860;
    }

    a:link {
        color: #3399ff
    }

    a:visited {
        color: #ffffff
    }

/*Framsidan*/

    .List-container {
        background: #000000;
    }

    .Row-container {
        background: #262626;
    }

    .Row-container:hover {
        background: #000000;
        transition: all .15s ease;
    }
    
    .Home-link-wrapper {
        background-color: #262626
    }
    
    .Home-link-wrapper:hover {
        background: #000000;
    }

/*Forumvyn*/

    body {
        background: #000000;
    }

    .Games-info {
        color: #cccccc;
        margin: 9px 40px;
    }

    .Row-white-container {
        background: #262626;
        color: #cccccc;
        padding: 4px;
    }

    .Row-links-container:hover .Row-white-container {
        background: #000000;
        transition: all .15s ease;
    }

    .Row-forum-container {
        padding: 0px 0 0;
        margin-bottom: 2px;
    }

    .Row-bubble-container {
        background: #262626;
        color: #cccccc;
        margin: 0px 12px 1px 0;
    }

    .Row-links-container:hover .Row-bubble-container {
        background: #000000;
        transition: all .15s ease;
    }

    .Row-title {
        color: #cccccc;
    }

/*Inne i trådar*/
    
    .Boop-container {
        background: #000000;
        color: #cccccc;
    }

    .PostItem-container {
        background: #000000;
        color: #cccccc;
    }

    .PostItem-body {
        background: #000000;
    }
    
    .PostItem-date {
        padding: 1px;
    }
    
    .PostItem-text {
        background: #262626;
        color: #e6e6e6;
    }

    .PostItem-text p img {
        max-width: 100%;
        max-height: 100%;
    }

    .PostItem-editorial {
        max-width: 100%;
        text-align: left;
    }

    .Post-container {
        max-width: 100%;
    }

    .PostList-boxlink {
        background: #262626;
    }

    .PostList-boxlink-active {
        background: #fd2860;
    }

/* Posteditorn */
    
    .editor-toolbar a {
        color: #e6e6e6 !important;
    }

    .CodeMirror {
        background: #444444;
        color: #eeeeee;
    }

/* Redax-sidan */
    
    .About-text {
        background: #262626;
        color: #e6e6e6;
    }
    
/* Login och registrering */

    .Signup-input {
        background: #444444;
        color: #eeeeee;
    }
}

@-moz-document domain("p.datadoghq.eu") {
/* 

NOTE: when uploading to userstyles.org, choose Mozilla Format / Export, and paste the contents, or the server will give a 404 (and show an error message about unintentionally global styles at the top of the page).

*/


/* Hide Datadog copyright notice */
  .version_number {
      display: none !important;
  }

  /* Hide header showing time selector and tv button (press `f` to toggle tv-mode and `esc` to go back) */
  body.tv_mode header.ui_layout_header--is-sticky {
      display: none;
  }

  div.screenboard.clearfix.free_layout {
    min-height: 100vh;
  }

  /* Datadog seems to always show scrollbars, even though tv-mode scales the contents to fit on the screen */
  body.tv_mode {
      overflow: hidden;
  }

  /* Hide mouse cursor in tv-mode (so we don't need to put it at the right side and trigger scrolling). */
  body.tv_mode * {
      cursor: none;
  }

/* Center incident counter */
#widget_13 .grid_text.big_number {
    padding-left: 6px;
    padding-bottom: 4px;
    font-size: 80px !important;
}


