@-moz-document domain("www.twitch.tv") {
  
	@font-face {
          font-family: Roboto;
          src: url('https://fonts.googleapis.com/css?family=Roboto&display=swap');
          font-weight: 400;
  		  font-style: Roboto;
	}
	@keyframes slide-bck-center {
		from {
			opacity: 0;
			transform: translateZ(0);
		}
		to {
			will-change: transform;
			opacity: 1;
			transform: translateZ(-400px);
		}
	}

	@keyframes newMessage {
		from {
			opacity: 0;
			transform: translateX(50px);
		}
		to {
			will-change: transform;
			opacity: 1;
			transform: translateX(5px);
		}
	}
	.chat-line__message:not(.special-message) {
		color: inherit;
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		padding: 15px !important;
		animation: newMessage 750ms forwards;
	}
	.chat-line__message:nth-child(2n):not(.special-message) {
		padding: 0 15px;
		background-color: rgba(0, 0, 0, .1) !important;
	}
	.chat-line__message:nth-child(2n + 1):not(.special-message) {
		padding: 0 15px;
		background-color: inherit !important;
	}
	.special-message {
		color: inherit;
		position: relative;
		box-sizing: border-box;
		padding: 15px !important;
		opacity: 1;
		overflow: hidden;
	}
	.chat-line__message-body--highlighted {
		color: white;
		position: relative;
		box-sizing: border-box;
		opacity: 1;
