@-moz-document domain('fusionfallretro.com'), domain('forums.fusionfalluniverse.com'), domain('fusionfalllegacy.com'), domain('fusionfalluniverse.com') {
  *:focus {
	outline: none;
  }

  ::-webkit-scrollbar {
	width: 20px;
	background-color: transparent;
  }

  /*::-webkit-scrollbar-button {
	width: 20px;
	height: 20px;
	background-image: url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/10c42631-39cf-47fa-9648-327c114d6641/ddfaakg-6853ae94-5649-46d1-9283-d2cc0fcd7481.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..gkXwpt6N9lXy-0V3m5Hp_HFA4xZV-nPAvonWChoO90k);
	background-position: 5% 3%;
	-webkit-box-shadow: inset 0 0 5px grey;
	box-shadow: inset 0 0 5px grey; 
    -webkit-border-radius: 10px;
    border-radius: 10px;
  }
    
  ::-webkit-scrollbar-button:hover {
	background-image: linear-gradient(rgb(0, 0, 0, 0.3), rgb(0, 0, 0, 0.3)), url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/10c42631-39cf-47fa-9648-327c114d6641/ddfaakg-6853ae94-5649-46d1-9283-d2cc0fcd7481.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..gkXwpt6N9lXy-0V3m5Hp_HFA4xZV-nPAvonWChoO90k);
	background-position: center;
  }
    
  ::-webkit-scrollbar-button:active {
	background-image: linear-gradient(rgb(0, 0, 0, 0.6), rgb(0, 0, 0, 0.6)), url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/10c42631-39cf-47fa-9648-327c114d6641/ddfaakg-6853ae94-5649-46d1-9283-d2cc0fcd7481.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..gkXwpt6N9lXy-0V3m5Hp_HFA4xZV-nPAvonWChoO90k);
	background-position: center;
  }*/

  ::-webkit-scrollbar-corner {
	background: transparent;
  }

  ::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 5px gray;
	-moz-box-shadow: inset 0 0 5px gray;
	box-shadow: inset 0 0 5px gray;
	-webkit-border-radius: 10px;
	border-radius: 10px;
	background-color: transparent;
  }

  ::-webkit-scrollbar-track:hover {
	-webkit-box-shadow: inset 0 0 5px green;
	-moz-box-shadow: inset 0 0 5px green;
	box-shadow: inset 0 0 5px green;
  }

  ::-webkit-scrollbar-track:active {
	-webkit-box-shadow: inset 0 0 5px darkgreen;
	-moz-box-shadow: inset 0 0 5px darkgreen;
	box-shadow: inset 0 0 5px darkgreen;
  }

  ::-webkit-scrollbar-thumb {
	background-image: url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/10c42631-39cf-47fa-9648-327c114d6641/ddfaakg-6853ae94-5649-46d1-9283-d2cc0fcd7481.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..gkXwpt6N9lXy-0V3m5Hp_HFA4xZV-nPAvonWChoO90k);
	-webkit-border-radius: 10px;
	border-radius: 10px;
  }

  ::-webkit-scrollbar-thumb:hover {
	background-image: linear-gradient(rgb(0, 0, 0, 0.3), rgb(0, 0, 0, 0.3)), url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/10c42631-39cf-47fa-9648-327c114d6641/ddfaakg-6853ae94-5649-46d1-9283-d2cc0fcd7481.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..gkXwpt6N9lXy-0V3m5Hp_HFA4xZV-nPAvonWChoO90k);
  }

  ::-webkit-scrollbar-thumb:active {
	background-image: linear-gradient(rgb(0, 0, 0, 0.6), rgb(0, 0, 0, 0.6)), url(https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/10c42631-39cf-47fa-9648-327c114d6641/ddfaakg-6853ae94-5649-46d1-9283-d2cc0fcd7481.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..gkXwpt6N9lXy-0V3m5Hp_HFA4xZV-nPAvonWChoO90k);
  }
}

@-moz-document domain('fusionfalluniverse.com') {
  ::selection {
	color: white;
	background-color: #9400D3;
  }

  ::-moz-selection {
	color: white;
	background-color: #9400D3;
  }
}

@-moz-document domain('fusionfallretro.com') {
  ::selection {
	color: #fff;
	background: #008b03;
  }

  ::-moz-selection {
	color: #fff;
	background: #008b03;
  }

  #status a {
	color: rgb(255, 225, 255) !important;
	text-decoration: none;
  }

  #status a:hover {
	text-decoration: underline;
  }

  #leftnav-list li a {
	color: #fff !important;
  }

  .main-content {
	background-color: rgba(0, 0, 0, 0.5);
	width: 95.29%;
	margin-left: 12.5px;
  }

  .page {
	color: rgb(255, 225, 255) !important;
  }

  .post_header {
	background: linear-gradient(rgb(0, 0, 0, 0.8), rgb(0, 0, 0, 0.9)), url(https://www.fusionfallretro.com/img/post-title.png);
	background-size: 100% 100%;
	border-top-left-radius: 15px;
	border-top-right-radius: 15px;
  }

  .post_content {
	background-image: linear-gradient(rgb(0, 0, 0, 0.5), rgb(0, 0, 0, 0.5)), url(https://www.fusionfallretro.com/img/bg_news_post.jpg);
	background-size: 100% 100%;
	color: #fff;
  }

  .post_content span {
	color: #fff !important;
  }

  .post_footer {
	background: linear-gradient(rgb(0, 0, 0, 0.8), rgb(0, 0, 0, 0.9)), url(https://www.fusionfallretro.com/img/footer.gif);
	background-size: 100% 100%;
	background-repeat: no-repeat;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
  }

  .credits-main-content {
	margin-left: 13.45px;
	width: 94.32%;
	border-left: 3px solid #008b03;
	border-right: 3px solid #008b03;
  }

  .credits-main-content td {
	border: 2px solid #008b03;
  }

  a {
	color: rgb(0, 225, 0) !important;
	text-decoration: none;
  }

  a:hover {
	text-decoration: underline;
  }

  span {
	color: rgb(0, 225, 0);
  }
}

@-moz-document domain('forums.fusionfalluniverse.com') {
  body {
	color: #aaa;
  }

  ::selection {
	color: white;
	background-color: #9400D3;
  }

  ::-moz-selection {
	color: white;
	background-color: #9400D3;
  }

  img#expandBoardsIcon {
	vertical-align: text-top;
  }

  h3.catbg img.icon, h4.titlebg img.icon {
	vertical-align: text-top;
  }

  .navigate_section ul {
	border-top: 2px solid purple;
	color: #ffffff50;
 }

  .navigate_section ul:hover {
	color: #fff;
  }

  .dropmenu li a.firstlevel span.firstlevel {
	font-weight: bold;
  }

  .dropmenu li ul {
	z-index: 90;
	display: none;
	position: absolute;
	width: 19.2em;
	font-weight: bold;
	border-bottom: 2px solid purple;
	background: linear-gradient(rgb(0, 0, 0, 0.8), rgb(0, 0, 0, 0.9)), url(https://www.forums.fusionfalluniverse.com/Themes/FFUniverse/images/theme/menu_gfx.png) 0 -130px no-repeat;
	padding: 7px 0 0 0;
  }

  .dropmenu li li {
	width: 19em;
	margin: 0;
	border-left: 2px solid purple;
	border-right: 2px solid purple;
  }

  .dropmenu li li a:hover, .dropmenu li li:hover>a {
	background: #d4dbe4;
	color: #000;
	text-decoration: none;
  }

  div #wrapper {
	color: #Fff;
	width: 927px;
	margin-right: 1px;
	background: linear-gradient(rgb(0, 0, 0, 0.3), rgb(0, 0, 0, 0.3)), url(https://www.forums.fusionfalluniverse.com/Themes/FFUniverse/images/bg_forums.gif);
	border-left: 3px solid purple;
	border-right: 3px solid purple;
  }

  div .cat_bar {
	background: rgb(128, 0, 128, 0.8) !important;
  }

  .roundframe {
	color: #fff;
	background-color: #050f1580 !important;
  }

  .roundframe dd input[type=text] {
	width: 100%;
  }

  .roundframe #bbcBox_message img {
	background-image: linear-gradient(rgb(0, 0, 0, 0.4), rgb(0, 0, 0, 0.5)), url(https://www.forums.fusionfalluniverse.com/Themes/FFRetro/images/bbc/bbc_bg.gif) !important;
  }

  .roundframe #bbcBox_message img:hover {
	background-image: url(https://www.forums.fusionfalluniverse.com/Themes/FFRetro/images/bbc/bbc_hoverbg.gif) !important;
  }

  .roundframe #postAdditionalOptionsHeader img {
	vertical-align: top;
  }

  .roundframe #smileyBox_message img:hover {
	-webkit-filter: brightness(150%);
	filter: brightness(150%);
  }

  .roundframe #message_resizer {
	visibility: hidden;
  }

  h1, h2, h3, h4, h5, h6 {
	color: #fff;
  }

 h3.catbg {
	background: linear-gradient(rgb(0, 0, 0, 0.8), rgb(0, 0, 0, 0.8)) !important;
  }

  h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a {
	color: #fff;
  }

  h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a:hover {
	color: #fff;
  }

  h3.catbg .ie6_header {
	color: #fff !important;
  }

  h3.catbg .ie6_header img {
	vertical-align: sub;
  }

  h4.catbg {
	color: #fff;
	background: linear-gradient(rgb(0, 0, 0, 0.8), rgb(0, 0, 0, 0.8));
  }

  h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg {
	background: linear-gradient(rgb(0, 0, 0, 0.8), rgb(0, 0, 0, 0.8));
  }

  .table_list tbody.content td.children {
	text-align: left;
	color: #555;
	font-size: 85%;
  }

  .table_list tbody.content td.children:hover {
	color: #fff;
  }

  tr.catbg th.first_th {
	text-align: center;
	background: rgb(128, 0, 128);
	border-top: 1px solid #fff;
	border-left: 1px solid #fff;
  }

  tr.catbg th {
	background: rgb(128, 0, 128);
	border-top: 1px solid #fff;
	border-right: 1px solid #fff
  }

  .table_grid tr.catbg, .table_grid tr.titlebg {
	font-size: 0.95em;
	border-bottom: 1px solid #fff;
  }

  .topic_table table thead {
	border-bottom: 1px solid #fff;
  }

  tr.catbg th.last_th {
