	border-left: 1px solid purple;
	border-right: 1px solid purple;
  }

  td.windowbg {
	text-align: center;
	color: #fff;
  }

  td.lefttext {
	text-align: center;
	color: #fff;
  }

  #mlist table tbody td.windowbg2 {
	text-align: center;
	color: #fff;
  }

  ul.quickbuttons a:hover {
	color: darkviolet;
  }

  .post img {
	max-width: 100%;
  }

