  }

  .title_bar .titlebg a:hover {
	color: #8b26aa;
  }

  .stickybg {
	background: #002440;
  }

  .stickybg2 {
	background: #003259;
  }

  td, th, tr {
	color: #fff;
  }

  dd span.smalltext a {
	color: red;
	cursor: help;
  }

  .smalltext, tr.smalltext th {
	color: white;
  }

  .table_list tbody.content td.info a.subject {
