@-moz-document domain("stud42.fr"){
  :root {
    	--main-background-color: #000000;
    	--secondary-background-color: #1A1B1A;
    	--main-header-color: #000000;
    	--my-td-color: #6666FF;
    	--regular-td-color: #003300;
    	--friend-td-color: #000033;
    	--button-color: #3472f7;
  }
  
  body {
    background: var(--main-header-color) !important;
  }
  
  body > header {
    	background: var(--main-header-color) !important;
    	border-bottom: 1px solid var(--main-header-color) !important;
  }
  
  div[class="head"] {
    	border-bottom: 1px solid var(--main-background-color) !important;
  }
  
  div[class="thumbs"] {
    border-bottom: 1px solid var(--main-background-color) !important;
    border-right: 1px solid var(--main-background-color) !important;
  }
  
  div[disconnected], div[disconnected] > div, div[appcontent="cluster"], div[class="head"], div[class="sidebar-menu"], div[class="thumbs"], div[class="col-sm-4"], div[class="col-sm-8"], div[class="row"], div[class="thumber"], div[appcontent="live"], div[class="modal-content"], div[class="modal-body"], div[class="modal-footer"], div[class="page-container"]{
    	background: var(--main-background-color) !important;
  }
  
  rect[class="highcharts-background"] {
    fill: var(--main-background-color) !important;
  }
  
  table, tbody, tr, td {
    background-color: var(--main-background-color) !important;
    border-bottom: 1px solid var(--main-background-color) !important;
    border-right: 1px solid var(--main-background-color) !important;       
    /*color: var(--main-background-color) !important;*/
  }
  

  td[connected] {
    background: var(--regular-td-color) !important;
    /*color: var(--regular-td-color) !important;*/
  }
  
  td[friend] {
    background: var(--friend-td-color) !important;
    /*color: var(--friend-td-color) !important;*/
  }

  td[me] {
    background: var(--my-td-color) !important;
  }
  
  td[pillar] {
    background: var(--secondary-background-color) !important;
  }

  .popover {
    background: var(--main-background-color) !important;
    border-color: var(--secondary-background-color) !important;
    margin: 0;
   }
}

