@-moz-document url("https://sspai.com/") {
.main-banner-box,
.minor-banner-box,
.pai_title,
.img_box.pai_img_box,
.recommend-card,
.card_content .right,
.indexRight.index_right > div > a {
    display: none !important;
}

.articleCard-box > div {
  max-width: 48%;
  margin: 0 5px;
  display: inline-block;
  overflow-y: hidden;
  overflow-x: hidden;
}

.img_box {
  width: 300px !important;
}

.pai_content {
    padding: 0 !important;
}

.pai_card_content {
    width: 100% !important;
    padding: 10px !important;
}


.card_content .left {
  display: -webkit-box !important;
}

.card_content .title.text_ellipsis2 {
  margin-bottom: 5px !important;
}

.card_content {
    width: auto !important;
    padding: 10px !important;
}

.follow_title {
    margin: 0 0 10px !important;
}

.swiper-container {
    padding: 20px 0 0;
}

.advertisementCardBox:hover .card_content {
    transform: translateX(-2px) !important;
}
}

@-moz-document domain("audiosparapeliculas2.foroactivo.com")
{

#logo, #page-header {
    display: none !important;
}

#wrap {
    background-color: #000000;
}

#fa_toolbar {
    background-color: #495248;
}

body, html {
    background-color: #161615;
}

.row2 {
    background-color: #000000;
}


}

@-moz-document url-prefix("https://myanimelist.net/anime"), url-prefix("https://myanimelist.net/manga") {
div.detail-characters-list div.picSurround a:not(.fw-n) img.lazyloaded {
    height: auto;
    width: 135%;
    margin-right: -30px;
}

div.detail-characters-list div.picSurround a.fw-n img {
    height: auto;
    width: 80%;
}
}

@-moz-document regexp("https://myanimelist\\.net/anime.*characters.*"), regexp("https://myanimelist\\.net/manga.*characters.*") {
td[valign=top]:not(.borderClass) {
    width: 27px;
}

td[align=right]:not(.borderClass) {
    width: auto;
}

div.picSurround a:not(.fw-n) img, div.picSurround a.fw-n img {
    height: auto;
    width: 80%;
}
}

@-moz-document url-prefix("https://myanimelist.net/character") {
td:first-child.borderClass[valign=top] {
    width: 27px;
}
div.picSurround a:not(.js-picture-gallery) img.lazyloaded {
    height: auto;
    width: 80%;
}
}

@-moz-document url-prefix("https://myanimelist.net/people") {
div.breadcrumb + div.normal_header + table td:nth-child(2).borderClass[valign=top] {
    position: absolute;
    margin-left: -15px;
}
div.breadcrumb + div.normal_header + table td:nth-child(3).borderClass[valign=top] > a, div.breadcrumb + div.normal_header + table td:nth-child(3).borderClass[valign=top] .spaceit_pad {
    margin-right: 30px;
}
div.breadcrumb + div.normal_header + table td:nth-child(4).borderClass[valign=top] {
    position: absolute;
    margin-left: -32px;
}
div.breadcrumb + div.normal_header + table td:first-child div.picSurround a:not(.js-picture-gallery) img.lazyloaded {
    height: auto;
    width: 50%;
}

div.breadcrumb + div.normal_header + table td:nth-child(4) div.picSurround a:not(.js-picture-gallery) img.lazyloaded {
    height: auto;
    width: 90%;
}
}

@-moz-document regexp("(http|https)://(www.|)(rarbgproxy.org|rarbgprx.org|rarbgmirror.xyz|rarbgto.org|rarbgtorrents.org|rarbg.is|rarbgmirror.com|rarbgmirror.org|rarbgproxied.org|rarbgaccessed.org|rarbg.com|rarbg.to).*") {
/* general start */
html {
    padding: 0;
    margin: 36px 0 0 0;
}

body {
    padding: 0;
    margin: 0;
    color: #dddddd;
    background: #26272a;
    background-color: rgb(38, 39, 42);
}

body > table {
    width: 100%;
    padding: 0 !important;
    margin: 0 !important;
}

table, tbody, tr, td {
    margin: 0 !important;
    padding: 0 !important;
    border-spacing: 0px;
}

br {
    display: none;
}

b {
    color: #dddddd;
}

td {
    border-bottom-color: rgb(50, 51, 56) !important;
}

td.header3 {
    color: #dddddd;
    background: #383838;
    background-color: rgb(56, 56, 56);
}

table.lista-rounded {
    color: #dddddd;
    background: #26272a;
    background-color: rgb(38, 39, 42);
}

table.header2, tr.header2, td.header2 {
    color: #dddddd;
    background: rgba(50, 51, 56, 1.0);
    background-color: rgba(50, 51, 56, 1.0);
}

table.lista tbody tr td.lista {
    border-bottom: 1px solid #323338;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(50, 51, 56);
}

td.lista {
    color: #dddddd;
}

.black {
    color: #dddddd;
}

a {
    text-decoration: none;
}

a:hover,
a:focus {
    color: #eeeeee;
    text-decoration: none;
}

a:link,
a:active {
    color: #dddddd;
}

a:link:hover,
a:link:focus,
a:active:hover,
a:active:focus {
    color: #ffffff;
}

a:visited {
    color: #999999;
}

a:visited:hover,
a:visited:focus {
    color: #b8b8b8;
}
/* general end */

/* probably torrent view rating start */
.unit-rating li a {
outline-color: rgb(38, 39, 42);
}
/* probably torrent view rating end */

/* login start */
input.pftext {
    background: #383838;
    background-color: rgb(56, 56, 56);
    border: 1px solid #383838;
    border-top-color: rgb(56, 56, 56);
    border-right-color: rgb(56, 56, 56);
    border-bottom-color: rgb(56, 56, 56);
    border-left-color: rgb(56, 56, 56);
}
/* login end */

/* search - search button start */
.btn-primary {
    color: #dddddd;
    background: #383838;
    background-color: rgb(56, 56, 56);
    border-top-color: rgb(56, 56, 56);
    border-right-color: rgb(56, 56, 56);
    border-bottom-color: rgb(56, 56, 56);
    border-left-color: rgb(56, 56, 56);
}
/* search - search button end */

/* search - result categories start */
td.lista > a + br + span {
    font-size: .8em !important;
    color: #aaaaaa !important;
    display: block;
}
/* search - result categories end */

/* search - general start */
input, select {
    color: #dddddd !important;
    background: #26272a !important;
    background-color: rgb(38, 39, 42) !important;
    border: 1px solid #888;
    border-top-color: rgb(136, 136, 136);
    border-right-color: rgb(136, 136, 136);
    border-bottom-color: rgb(136, 136, 136);
    border-left-color: rgb(136, 136, 136);
}

table.lista2t tr.lista2 {
    background: #26272a !important;
    background-color: rgb(38, 39, 42) !important;
    border-width: 0px !important;
}

table.lista2t tr.lista2:nth-child(2n) td {
    color: #dddddd !important;
    background: rgba(50, 51, 56, 1.0) !important;
    background-color: rgba(50, 51, 56, 1.0) !important;
}

table.lista2t tr.lista2:nth-child(2n+1) td {
    color: #dddddd !important;
    background: rgba(60, 61, 66, 1.0) !important;
    background-color: rgba(60, 61, 66, 1.0) !important;
}

td.header6 {
    color: #dddddd;
    background: #505050;
    background-color: rgb(70, 70, 70);
}

table.lista2t {
    border-bottom: 0px;
    border-bottom-width: 0px;
    border-left: 0px;
    border-left-width: 0px;
}

table.lista2t tr.lista2 td {
    border-bottom: 0px;
    border-bottom-width: 0px;
}

table.lista2t tr.lista2 td {
    border-right: 0px;
    border-right-width: 0px;
}

div.content-rounded > table.lista-rounded table.lista2t tr:first-of-type td:first-of-type {
    border-top-left-radius: 5px;
    border-style: solid;
    border-width: 0px;
}

div.content-rounded > table.lista-rounded table.lista2t tr:first-of-type td:last-of-type {
    border-top-right-radius: 5px;
    border-style: solid;
    border-width: 0px;
    padding: 0 .75em 0 0 !important;
}

div.content-rounded > table.lista-rounded table.lista2t tr:last-of-type td:first-of-type {
    border-bottom-left-radius: 5px;
    border-style: solid;
    border-width: 0px;
}

div.content-rounded > table.lista-rounded table.lista2t tr:last-of-type td:last-of-type {
    border-bottom-right-radius: 5px !important;
    border-style: solid !important;
    border-width: 0px !important;
    border-color: rgb(50, 51, 56) !important;
    border-color: rgba(50, 51, 56, 1.0) !important;
}

td.header6 {
    border-right: 0px;
    border-right-width: 0px;
}

td.header2 a {
    color: #dddddd !important;
}

td.lista2 {
    border: 0px !important;
    border-top-width: 0px !important;
    border-right-width: 0px !important;
    border-bottom-width: 0px !important;
    border-left-width: 0px !important;
    color: #dddddd !important;
    background-color: #444444 !important;
}

td.lista2 > table > tbody > tr > td {
    background-color: #444444 !important;
}

td#description > div:first-of-type {
    padding: 3px 0px 0px 0px !important;
    margin: 0px 0px 0px 0px !important;
    border-style: solid !important;
    border-width: 1px 1px 1px 1px !important;
    border-color: #606060 !important;
    width: 100% !important;
    background-color: #323338 !important;
}

td#description > div:first-of-type > b {
    padding: 0px 3px 0px 3px !important;
    margin: 0px 0px 5px 0px !important;
}
/* search - general end */

/* search terms start */
h1.black {
    font-size: .9em;
    font-weight: normal;
    margin-bottom: 0;
}
/* search terms end */

/* page links start */
#pager_links {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: row;
}

#pager_links a {
    border: 1px solid #888888;
    border-radius: 5px;
    color: #dddddd;
    background-color: #444444;
    font-size: 1em;
    margin: 0 .5em;
    align-self: center;
}

#pager_links a:hover {
    background-color: #666666;
}

#pager_links b {
    border: 1px solid #888888;
    border-radius: 5px;
    background-color: #888888;
    color: #444444;
    font-size: 1em;
    margin: 0 .5em;
    align-self: center;
}
/* page links end */

/* search box start */
.searchTorrent, .formBox, .formBox2 {
    color: #dddddd;
    background: rgba(50, 51, 56, 1.0);
    background-color: rgba(50, 51, 56, 1.0);
    border: 1px solid #7fa2c6;
    border-top-color: rgb(50, 51, 56);
    border-right-color: rgb(50, 51, 56);
    border-bottom-color: rgb(50, 51, 56);
    border-left-color: rgb(50, 51, 56);
    padding: 6px 2px 4px 2px;
}

.searchTorrent {
    border-radius: 0 0 5px 5px;
}

.searchTorrent br {
    display: block !important;
}

.searchTorrent input {
    border-radius: 5px !important;
    -moz-border-radius: 5px !important;
    -webkit-border-radius: 5px !important;
}

.searchTorrent tr:first-of-type > td:nth-of-type(1) > input#searchInput {
    border-radius: 5px;
}

.searchTorrent button.btn-primary {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
}

.searchTorrent tr:first-of-type > td:nth-of-type(3) > a#shadvbutton {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
}

#divadvsearch {
    margin: 0 !important;
    padding-top: 8px !important;
}

.divadvscat,
.divadvclearcats {
    height: auto;
    margin-bottom: 2px;
    margin-left: 2px;
    font-size: .9em;
}

.divadvscat a,
.divadvscat a:active,
.divadvscat a:visited,
a.divadvclearcats,
a.divadvclearcats:active,
a.divadvclearcats:visited {
    color: #cfcfcf !important;
    text-decoration: none;
}

.divadvscat a:focus,
.divadvscat a:hover
a.divadvclearcats:focus,
a.divadvclearcats:hover {
    color: #ffffff !important;
    text-decoration: none;
}
/* search box end */

/* mouseover preview start */
div#overlib {
    padding: 2px 2px;
    background-color: rgba(255, 255, 255, 0.5);;
}
/* mouseover preview end */

/* remove duplicate search terms at bottom start */
body > table:nth-of-type(3) > tbody:first-of-type > tr:first-of-type > td:nth-of-type(2) > div.content-rounded > table.lista-rounded > tbody:first-of-type > tr:nth-of-type(4) {
    display: none;
}
/* remove duplicate search terms at bottom end */


/* remove logo and login section start */
body > table:first-of-type {
    display: none;
}
/* remove logo and login section end */

/* remove top nav bar start */
body > table:nth-of-type(2) {
    display: none;
}
/* remove top nav bar end */

/* remove news starts */
#newsRight {
    display: none;
}
/* remove news end */

/* relocate side nav starts */
body > table:nth-of-type(3) {
    color: #dddddd;
    background: #26272a !important;
    background-color: rgb(38, 39, 42) !important;
}

body > table:nth-of-type(3) > tbody > tr > td:nth-of-type(2) {
    position: relative;
    z-index: 1;
    color: #dddddd;
    background: #26272a !important;
    background-color: rgb(38, 39, 42) !important;
    padding: 0 2em !important;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type {
    position: absolute;
    z-index: 2;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 36px;
    margin: 0 !important;
    padding: 0 !important;
    overflow-y: hidden;
    overflow-x: hidden;
    display: flex;
    justify-content: center;
    background: #26272a !important;
    background-color: rgb(38, 39, 42) !important;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table {
    display: flex;
    justify-content: center;
    background: #26272a !important;
    background-color: rgb(38, 39, 42) !important;
    margin: 0 auto;
    padding: 0 !important;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody {
    display: flex;
    justify-content: center;
    color: #dddddd;
    background: rgba(50, 51, 56, 1.0);
    background-color: rgb(50, 51, 56);
    background-color: rgba(50, 51, 56, 1.0);
    margin: 0 auto;
    padding: 0 !important;
    border-radius: 0 0 5px 5px;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr {
    margin: .25em .5em !important;
    display: inline-block;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-child(n+9):nth-child(-n+15) {
    display: none;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr > td > a {
    color: #dddddd;
    text-decoration: none;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr > td > a:hover,
body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr > td > a:focus {
    color: #ffffff;
    text-decoration: none;
}

td.header5 {
    color: #dddddd;
    background: rgba(50, 51, 56, 1.0);
    background-color: rgb(50, 51, 56);
    background-color: rgba(50, 51, 56, 1.0);
    -moz-border-radius-topleft: 3px;
    -moz-border-radius-bottomleft: 3px;
    -moz-border-radius-topright: 3px;
    -moz-border-radius-bottomright: 3px;
    -webkit-border-top-left-radius: 3px;
    -webkit-border-bottom-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -webkit-border-bottom-right-radius: 3px;
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
    border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
    padding: 0 3px !important;
}

html > body > table:nth-of-type(3) .header5 > a {
    font-size: 1.5em;
    position: inherit;
    display: block;
    padding: 3px 3px;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(1) {
    order: 1;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(2) {
    order: 2;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(3) {
    order: 8;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(4) {
    order: 3;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(5) {
    order: 5;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(6) {
    order: 4;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(7) {
    order: 6;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody > tr:nth-of-type(8) {
    order: 7;
}

body > table:nth-of-type(3) > tbody > tr > td:first-of-type > table > tbody::after {
    order: 0;
    content: "RARBG";
    font-size: 1.5em;
    font-weight: 700;
    color: #1f4d96;
    -webkit-text-fill-color: #1f4d96; /* Will override color (regardless of order) */
    -webkit-text-stroke-width: 1px;
    -webkit-text-stroke-color: #ffffff;
    display: block;
    position: inherit;
    flex: 0 0 auto;
    align-self: center;
    margin-left: 9px;
    margin-right: 6px;
}

/* relocate side nav ends */

/* remove white star rating start */
div.ratingblock > div:first-of-type > ul:first-of-type {
    display: none !important;
}
/* remove white star rating end */

/* fix release date font color start */
td.lista > span[itemprop=releaseDate] {
    color: #dddddd !important;
}
/* fix release date font color end */

/* remove footer start */
body > table:last-of-type ~ div, body > table:last-of-type ~ br {
    display: none !important;
}
/* remove footer end */
}

@-moz-document regexp("(http|https)://(www.|)(rarbgproxy.org|rarbgprx.org|rarbgmirror.xyz|rarbgto.org|rarbgtorrents.org|rarbg.is|rarbgmirror.com|rarbgmirror.org|rarbgproxied.org|rarbgaccessed.org|rarbg.com|rarbg.to)/torrent/.*") {
/* torrent view - general start */
p,
strong,
td.lista,
td.lista > span {
    font-size: 1em !important;
    font-weight: normal;
    color: #aaaaaa;
}
/* torrent view - general end */

/* torrent view - show/hide files start */
#msgfile {
    margin-left: 0;
}
/* torrent view - show/hide files end */

/* torrent view - posters start */
img[src*="posters"] {
    margin-left: .5em;
}
/* torrent view - posters end */

/* torrent view - rating start */
div.ratingblock,
div.ratingblock > div:first-of-type {
    margin-left: 0;
}
/* torrent view - rating end */

/* torrent view - all direct child tds of all tr which are direct children start */
div.content-rounded:first-of-type > table.lista-rounded:first-of-type > tbody:first-of-type > tr:nth-of-type(2) > td:first-of-type > div:first-of-type > table.lista:first-of-type > tbody:first-of-type > tr > td {
    font-size: 1em;
}
/* torrent view - all direct child tds of all tr which are direct children end */

/* torrent view - base table start */
div.content-rounded:first-of-type > table.lista-rounded:first-of-type > tbody:first-of-type > tr:nth-of-type(2) > td:first-of-type > div:first-of-type > table.lista:first-of-type {
    border: none !important;
    border-width: 0px !important;
    border-spacing: 0px !important;
    border-collapse: collapse !important;
}
/* torrent view - base table end */

/* torrent view - all tds and trs start */
div.content-rounded:first-of-type > table.lista-rounded:first-of-type > tbody:first-of-type > tr:nth-of-type(2) > td:first-of-type > div:first-of-type > table.lista:first-of-type > tbody:first-of-type tr,
div.content-rounded:first-of-type > table.lista-rounded:first-of-type > tbody:first-of-type > tr:nth-of-type(2) > td:first-of-type > div:first-of-type > table.lista:first-of-type > tbody:first-of-type td {
    border: none !important;
    border-width: 0px !important;
    border-spacing: 0px !important;
    border-collapse: collapse !important;
}
/* torrent view - all tds and trs start */

/* torrent view - all direct child tds of any tr start */
div.content-rounded:first-of-type > table.lista-rounded:first-of-type > tbody:first-of-type > tr:nth-of-type(2) > td:first-of-type > div:first-of-type > table.lista:first-of-type > tbody:first-of-type tr > td {
    border: none !important;
    border-width: 0px !important;
    border-spacing: 0px !important;
    border-collapse: collapse !important;
    border-style: solid !important;
    border-color: rgba(50, 51, 56, 1.0) !important;
    padding: 2px 5px !important;
}
/* torrent view - all direct child tds of any tr end */

/* torrent view - description start */
td#description {
    padding: 1em !important;
    margin: 0 !important;
    text-align: left;
    white-space: pre-wrap;
    display: flex;
    flex-wrap: wrap;
}

td#description br {
    display: none;
}

td#description > b {
    min-width: calc(100%);
    max-width: calc(100%);
    width: calc(100%);
    height: 1em;
    flex-grow: 1;
    flex-shrink: 1;
    flex-basis: 0;
    flex-direction: column;
}

td#description > img[src*="totalstatic"] {
    height: 18px;
