    background: #1B1B1B;
}
.typical {
    color: #ddd;
}
.conthead.blog .date {
    color: #aaa;
}
a, a:link {
    color: #eee;
}
a:hover {
    color: #ed1c24;
}
a.dark:hover {
    color: #ed1c24;
}
.lite {
    color:#959595 !important;
}
.black {
    color:     #b50a0a !important;
}
