    background-color: #171717;
	border: solid 1px    #4d4d4d;
}
.carouselnav .num {
    color: #e6e6e6    
}
.carouselnav li.current, .carouselnav li.current .num {
    color: #ed1c24;
}
.preview .prevcontent {
    background:     #393939;
}
.secondsubmenu {
    background:     #222;
}
.secondmenusection:hover a.menulink {
    background:     #222;
}
.carouselnav li{
    background-color: transparent;
    border: solid 1px #4d4d4d;
}
.carouselnav .wsico {
    color: #707070;
}
.inpselblock {
    background: none;
    color: #eee;
}
.commBlock {
    color: #eee !important;
}
.hotlinks h2 {
    background: #2B2B2B;
}
.preview.hover .prevcontent {
    box-shadow: 0 0 0 0.25em #1B1B1B;
}
.preview .mediamenu {
    border: none;
    
}
.preview .mediamenu a {
    background: #2B2B2B;
    color: #fff;
    border-left: none;
}
.dark {
    color:     #88868c !important;
}
.preview .title {
    color:    #b9b9b9;
}
.preview .mediamenu a .text {
    color: #fff;
}
.preview .mediamenu a .size {
    color: #888;
}
.conthead .person .name {
    color: #ececec;
}
.multimedia .mediamenu a {
    background: #2f2f2f;
    color:    #b9b9b9
}
.multimedia .mediamenu a .text {
    color: #fff;
}
.multimedia .mediamenu a .size {
    color: #888;
}
.aboutbroadcast {
    background: #3e3e3e;
}
.aboutbroadcast .text {
    color: inherit;
}
}

