    Original Red-Yellow color

    --main-color-1: #cf433f;
    --main-color-2: #e09346;
    --main-color-3: #f6d330;
*/

:root {
    --main-color-1: #cf433f;
    --main-color-2: #e09346;
    --main-color-3: #f6d330;
    --text-color-black: #1d2129;
