/*上層*/
.sky
{
    background-color: rgba(0, 0, 0, 0.3);
}
.mainmenu
{
    background-color: rgba(0, 180, 216, 0.3) !important;
}
.mainmenu li
{
    border-top: 1px solid rgba(0, 0, 0, 0);
