@-moz-document domain("wikipedia.org") {
:root {
    --foreground: #E0E2E4;
    --background: #293134;
    --line-numbers: #81969A;
    --comment: #7D8C93;
    --keyword: #93C763;
    --operators: #E8E2B7;
    --strings: #EC7600;
    --numbers: #FFCD22;
    --name: #678CB1;
    --preprocessor: #A082BD;
    --selection-background: #804000;
}

/* Comments. */
.mw-highlight .ch, .mw-highlight .c1, .mw-highlight .cm, .mw-highlight .c {
    color: var(--comment);
}
/* Foreground */
.mw-highlight .nv, .mw-highlight .nf, .mw-highlight .p, .mw-highlight .n, .mw-highlight .nx, .mw-highlight .na, .mw-highlight .nl {
    color: var(--foreground);
}
.mw-highlight .o {
    color: var(--operators);
}
.mw-highlight .s2, .mw-highlight .s1, .mw-highlight .cpf, .mw-highlight .s, .mw-highlight .sc {
    color: var(--strings);
}
.mw-highlight .se, .mw-highlight .mi, .mw-highlight .m, .mw-highlight .mf, .mw-highlight .mh {
    color: var(--numbers);
}
.mw-highlight .nb, .mw-highlight .k, .mw-highlight .kt, .mw-highlight .kd {
    color: var(--keyword);
}
.mw-highlight .nn, .mw-highlight .nc, .mw-highlight .bp, .mw-highlight .ss {
    color: var(--name);
}
.mw-highlight pre {
    color: var(--foreground);
    background: var(--background) !important;
}
.mw-highlight .lineno {
    color: var(--line-numbers);
}
.mw-highlight .cp, .mw-highlight .nd {
    color: var(--preprocessor);
}
.mw-highlight *::selection {
    color: var(--foreground);
    background-color: var(--selection-background);
}
}

