/*===========================================================
Name: 晋江论坛苹果绿
Author: CIRER
Update: 2020.1.10
Description:  Userstyle for bbs.jjwxc.net or bbs.jjwxc.com
Works with 
　■ Add-on "Stylish"
　○ https://addons.mozilla.org/en-US/firefox/addon/2108/
　■ Add-on "Adblock Plus"
　○ https://addons.mozilla.org/en-US/firefox/addon/1865/
===========================================================*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://bbs.jjwxc.com/newpost") {
TABLE[bordercolor="#808000"] {
    border: solid 1px green!important;
}
TABLE[bordercolor="#808000"] td{
    border: none !important; 
   
}
#subidform{
   border:none!important;
    
}
}

@-moz-document url("https://bbs.jjwxc.com/index.php"), url("https://bbs.jjwxc.com/"), url("https://bbs.jjwxc.net/") {
#navlist {
    background: none !important;
}
#adnav {
    border: none !important;
}
#top02 {
    margin-bottom: -80px !important;
}
#top02 DIV {
    height: 0px !important;
}
#top02 IMG,
.p1,
IMG[src="img/bg1.jpg"] {
    display: none !important;
}
#top02 .p2 {
    background: none !important;
    width: 100% !important;
}
#top02 .p2 STRONG {
    border-bottom: dashed 1px green !important;
}
.f1.le {
    margin-top: 10px !important;
}
.cont03 .headline {
    padding: 10px 5px 4px 10px !important;
}
.cont01,
.cont02,
.cont03,
.cont04,
.cont05 {
    background: none !important;
    border: solid 1px green !important;
}
.headline {
    background: green !important;
    color: #FFF !important;
}
HR[size="1"][noshade] {
    width: auto !important;
}
#bottom {
    clear: both !important;
    border: none !important;
}
}

@-moz-document domain("bbs.jjwxc.net"), domain("bbs.jjwxc.com") {
/*背景色*/
body,
td,
div {
    background-color: #C7EDCC !important;
}
body {
    margin: 0 !important;
}

/*字体表*/
* {
    font-family: "Microsoft Yahei", Arial, Helvetica, sans-serif !important;
}



/*链接*/
A {
    color: black !important;
    text-decoration: none !important;
}
A:hover {
    background: white !important;
}

/*隐藏表格边框*/
TABLE[bgcolor="#E8F3FF"],
/*文章最后的导航表格*/
TABLE[bgcolor="#999999"]/*版块导航表格*/
{
    background: none !important;
}

/*修正灰色字*/
FONT[color="#999999"] {
    color: green !important;
    font-size: 14px ! important;
}

/*修正各个分版块表格样式*/
TABLE[bordercolor="#FFFFFF"],
TABLE[bordercolor="#ffffff"] {
    border: 0! important;
    background: #999933!important;
}
TABLE[bordercolor="#ffffff"] TD {
    background: #C7EDCC !important;
    border: 0 !important;
}
TR[bgcolor="#fff5ec"] TD {
    background: #99CC88 !important;
}

/*隐藏横幅广告、论坛LOGO、红心、囧囧商城文字广告*/
.smalltitle,
#playBox,
.block,
#goo,
#google_image_div,
#google_ads_frame1,
#google_ads_frame2， #adContent,
#dingbuFix img,
#tongLanUper,
#tongLanDown IMG,
IMG[src="img/heart.gif"],
FONT[color="#ff99ff"],
TABLE[width="20%"],
FONT[color="#0d7328"],
TR[bgcolor="#F9CCDE"],
.heartimg,
IFRAME#aswift_0,
DIV[style="width:728px; height:90px; overflow: hidden;"] {
    display: none ! important;
}

/*文本框修正*/
TEXTAREA,
INPUT {
    text-shadow: #777777 1px 1px 1px !important;
}
BUTTON,
INPUT,
SELECT,
TEXTAREA {
    appearance: none !important;
    border: 1px solid green !important;
    background: none !important;
}

/*加宽*/
TABLE[width="760"],
TABLE[width="764"] {
    width: 800px !important;
}

/*回复框&发帖框*/
TABLE[bordercolor="#FFF9E1"] {
    border: double green 3px !important;
    padding: 10px !important;
}
TABLE[bordercolor="#FFF9E1"] td {
    border: none !important;
}

/*去掉各个分版块“版规”两字上的横线*/
TD[bgcolor="#FFCC99"] hr {
    display: none ! important;
}
/*子论坛导航条*/
#subidform {
    border-top: none !important;
    padding: 3px 0 3px 0 !important;
}
}

@-moz-document url-prefix("https://bbs.jjwxc.net/showmsg"), url-prefix("https://bbs.jjwxc.com/showmsg") {
#google_bbs_top,
#imgurl {
    display: none ! important;
}
#boardname {
    padding: 20px 5px 8px 5px !important;
}


/*修正撑破内壁*/
TABLE[width="100%"] {
    table-layout: fixed !important;
}
.read {
    font-size: 18px !important;
    /*字号*/
    line-height: 1.8em !important;
    word-wrap: break-word !important;
}

/*链接还原为蓝色*/
A {
    color: blue !important;
}

/*标题*/
TD[bgcolor="#e8f3ff"] {
    text-shadow: #888888 0px 1px 2px;
    font-size: larger!important;
    font-weight: bold !important;
    padding-top: 5px !important;
    padding-bottom: 10px !important;
    border-top: solid 2px green !important;
    letter-spacing: 0.2em !important;
}
DIV#topic {
    padding-bottom: 5px !important;
    margin-bottom: 7px !important;
}

/*登录状态*/
#loginStatus {
    width: 150px !important;
}


/*页码*/
#pager_top B {
    color: #ff1e00 !important;
}
#pager_top A:hover {
    border: solid blue 1px !important;
}
#pager_top {
    position: fixed !important;
    left: 0 !important;
    top: 30px !important;
    background: #F9F9F9 !important;
    width: 60px !important;
    border: green 1px solid !important;
    border-left: none !important;
    padding: 15px !important;
    border-radius: 0 10px 10px 0 !important;
    opacity: 0.9;
    color: green !important;
    text-align: left !important;
    font-weight: bold !important;
    box-shadow: #666666 0 0 10px !important;
}

/*图像居中*/
.read IMG {
    display: block !important;
    margin-left: auto !important;
    margin-right: auto !important;
}

.read {
    border-bottom: dashed 1px green !important;
    padding: 5px 15px 5px 15px !important;
}


/*头像为圆形*/
.image1,
.image2,
.image6,
.image7,
.image8,
.image9,
.image10,
.image12,
.image13,
.image14,
.image15,
.image16,
.image17 {
    width: 44px !important;
    height: 44px !important;
    border-radius: 50% !important;
}

/*分隔线*/
hr[width="760"] {
    color: green!important;
    width: 800px !important;
}

hr[color="#99CC00"] {
    color: green!important;
    margin-top: 3px !important;
}
}

@-moz-document url-prefix("https://bbs.jjwxc.net/search"), url-prefix("https://bbs.jjwxc.com/search") {
/*搜索结果*/
TABLE[width="80%"],
TABLE[bordercolor="#808000"] {
    width: 900px !important;
    border: none !important;
}
TABLE[bordercolor="#808000"] TD {
    border: none !important;
    border-bottom: solid 1px green!important;
    padding: 3px !important;
}
TABLE[bordercolor="#808000"] A {
    color: green !important;
}

hr {
    width: 900px !important;
    color: green !important;
}
}

