@-moz-document domain(gitlab.tu-ilmenau.de) {
/*
     * Name:    TU Ilmenau gitlab Dark Mode
     * Author:  wobbba
     * Version: v0.1.1
     */

:root {
    --main-bg-color: #333;
    --sec-bg-color: #222;
    --main-text-color: #dddddd;
    --link-color: #5599ff;
    --link-color-visited: #aa88ee;
    --main-btn-color: #407045;
}

body, th, .file-content, .js-file-title {
	background-color: var(--main-bg-color) !important;
}

td {
    background-color: var(--sec-bg-color) !important;
}

h1, h2, h3, h4, h5, h6, p, table {
	color: var(--main-text-color) !important;
}

a {
	color: var(--link-color) !important;
}

a:visited {
	color: var(--link-color-visited) !important;
}

.info-well {
	background: var(--main-bg-color);
}

.btn {
	background-color: var(--main-btn-color);
    color: var(--main-text-color) !important;
}
}
