@-moz-document regexp("https?:\\/\\/weathernews\\.jp\\/onebox\\/[0-9.]+\\/[0-9.]+.*") {
/*======================================
  weathernews pinpoint forecast ja jp compact
  作り直し: 2019-07-31
  2019-08-15 台風情報挿入でレイアウト崩れ直し
  2019-10-24 Adblock 警告を非表示
  2019-11-22 10日間天気のコンパクト化追加
  2020-02-08 崩れ修正
  ======================================*/
/*--- unwanteds ---*/
[id*="-ad-"], [class*="-ad-"],
body>div[style^="bottom"], /* adblock warning 2019-10-24 */
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: initial; font-size: initial; font-weight: initial;
 line-height: 1.4em;
 }
div.section {max-width: none !important;}
body, div, p, span, h1, h2, h3, h4, h5, h6, ol {margin-top: inherit; margin-bottom: inherit;}
html, body {width: auto; height: auto;}
h1, h2, h3, h4 {padding: 0;}

.inner {padding: 0;}


/* header */
.header__bar {float: left; padding: 0;}
.global-search {float: right; max-width: unset; margin: 0; padding: 0;}

/* main */
article#content {display: block; width: auto; max-width: none;} /* 2020-02-08 */
article#main {float: left;}
aside#sub {display: block; float: right;}

.weather-day {max-width: unset;}

#main [class="block pc"] {display: none;}

/* データカード */
#main {display: flex; flex-wrap: wrap; justify-content: center;}
#main > section {float: left; margin: 0.2em; padding: 0.5em; border-radius: 0.5em;}
#main > section .tit-01 {padding: 0;}

#main > section:first-child,
#main > section:nth-child(2) {display: none;} /* flash版リンクバナー */
#main > section[style="padding-bottom:0px"] {width: 100%;} /* 市町村の天気 ページタイトル */
 #main > section[style="padding-bottom:0px"] * {display: inline;}
#main > section.inner {order: 1;} /* 市町村の天気 1時間毎データ表 */
#main > section.inner + section {display: none;} /* 不明バナー */
#main > section.inner + section + section {order: 3;} /* 市町村付近の天気 概況 */
 #main > section.inner + section + section .btn {display: none;} /* グラフリンクボタン */
 #main > section.inner + section + section .weather-now {max-width: unset; margin-bottom: 0; display: block;}
 #main > section.inner + section + section .weather-now > * {display: block;}
#main > section.inner + section + section + section {order: 3;} /* 市町村付近の週間天気データ表 */
 #main > section.inner + section + section + section .weather-weekly {width: unset; max-width: unset; margin-top: 0;}
#main > section.no-ja {display: none;} /* ライブ動画→非表示 */
#main > section.no-global {order: 1;} /* 市町村付近の1時間毎観測データ表 */
  
/* 10日間の天気一覧表のセル幅調整 2019年11月22日追加 */
[class^="weather-10day__"] {margin: 0; padding: 0;}
.weather-10day__head, .weather-10day__body {width: auto;}
.weather-10day__head > p:nth-child(1), .weather-10day__day {width: 5em !important;} /* 日付曜日 */
.weather-10day__head > p:nth-child(2), .weather-10day__icon {width: 3em;} /* 天気(アイコン) */
.weather-10day__head > p:nth-child(3), .weather-10day__h {width: 7em;} /* 最高気温 */
.weather-10day__head > p:nth-child(4), .weather-10day__l {width: 7em;} /* 最低気温 */
.weather-10day__head > p:nth-child(5), .weather-10day__r {width: 7em;} /* 降水確率 */
.weather-10day__icon img {width: 38px; height: 28px;} /* 天気アイコン */
[class^="weather-10day__"] br {content: "";} /* 表内改行無効化 */
[class^="weather-10day__"] br::before {display: inline; content: " ";}


/* 1時間毎の天気一覧表のセル幅調整 */
#main > *:not(:last-child) {padding-bottom: 0;}
[class^="weather-day__"] {padding: 0;}
.weather-day__head, .weather-day__item {width: auto;}
.weather-day__head > p:nth-child(1), .weather-day__time {width: 4em;} /* 時刻 */
.weather-day__head > p:nth-child(2), .weather-day__icon {width: 3em;} /* 天気(アイコン) */
.weather-day__head > p:nth-child(3), .weather-day__r {width: 4em;} /* 降水量 */
.weather-day__head > p:nth-child(4), .weather-day__t {width: 3em;} /* 気温 */
.weather-day__head > p:nth-child(5), .weather-day__w {width: 6em;} /* 風 */
.weather-day__icon img {width: 38px; height: 28px;} /* 天気アイコン */
.weather-day__w br {content: "";} /* 表内改行無効化 */
.weather-day__w br::before {display: inline; content: " ";}
.tbl_more {display: table !important;} /* その先の時間 */
#fcst_more {display: none;} /* もっと表示ボタン */

/* 1時間毎の観測データ表 */
.obs_tbl_more {display: table-row !important;}
#obs_more {display: none;} /* もっと表示ボタン */
#sharing .sns-share {display: none;} /* シェアリングボタン */


/* 週間天気データ表 */
.weather-weekly__thumb img {width: 38px; height: 28px;}


/* side bar section */
#sub > *:not(:last-child) {margin-bottom: 0;}
#sub > section:first-child,
#sub > section:nth-child(2) {display: none;} /* 広告バナー */
}

