@keyframes BTransition { 
	0% { background: #f00 }
	5% { background: #f00 }
	15% { background: #f80 }
	25% { background: #ff0 }
	35% { background: #8f0 }
	45% { background: #0f8 }
	55% { background: #08f }
	65% { background: #00f }
	75% { background: #80f }
	85% { background: #f08 }
	95% { background: #f00 }
	100% { background: #f00 }
}

@keyframes BgTransition { 
	0% { background-color: #f00 }
	5% { background-color: #f00 }
	15% { background-color: #f80 }
	25% { background-color: #ff0 }
	35% { background-color: #8f0 }
	45% { background-color: #0f8 }
	55% { background-color: #08f }
	65% { background-color: #00f }
	75% { background-color: #80f }
	85% { background-color: #f08 }
	95% { background-color: #f00 }
	100% { background-color: #f00 }
}

/* KeyFrames for text and currentColor */
@keyframes ColorTransition { 
	0% { color: #f00 }
	5% { color: #f00 }
	15% { color: #f80 }
	25% { color: #ff0 }
	35% { color: #8f0 }
	45% { color: #0f8 }
	55% { color: #08f }
	65% { color: #00f }
	75% { color: #80f }
	85% { color: #f08 }
	95% { color: #f00 }
	100% { color: #f00 }
}

@keyframes BorderColorTransition { 
	0% { border-color: #f00 }
	5% { border-color: #f00 }
	15% { border-color: #f80 }
	25% { border-color: #ff0 }
	35% { border-color: #8f0 }
