@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {


	a[href*="#skill"], a[href*="&ll="]{ 
		font-weight:bold ;
	}

	/*
	* OGNI SKILL NERA AL PASSAGGIO DEL MOUSE
	* EVERY SKILL BLACK ON HOVER
	*/
	a:hover[href*="#skill"], a:hover[href*="&ll="]{
		color:black ;
		font-weight:bold ;
	}

	/*
	* COLORAZIONE DELLE SKILL DI CARATTERE
	* COLOR FOR Agreeability,Honesty,Aggressiveness
	* note: if you want to disable this part cut it off :D
	*/
	a[href*="lt=gentleness"],
	a[href*="lt=aggressiveness"],
	a[href*="lt=honesty"]{
		padding:1px 3px;
	}

	a[href*="lt=honesty&ll=0#honesty"],
	a[href*="lt=aggressiveness&ll=4#aggressiveness"],
	a[href*="lt=gentleness&ll=0#gentleness"]{ background-color:red; }

	a[href*="lt=honesty&ll=1#honesty"],
	a[href*="lt=aggressiveness&ll=3#aggressiveness"],
	a[href*="lt=gentleness&ll=1#gentleness"]{ background-color:orange; }

	a[href*="lt=honesty&ll=2#honesty"],
	a[href*="lt=aggressiveness&ll=2#aggressiveness"],
	a[href*="lt=gentleness&ll=2#gentleness"]{ background-color:yellow; }

	a[href*="lt=honesty&ll=3#honesty"],
	a[href*="lt=aggressiveness&ll=1#aggressiveness"],
	a[href*="lt=gentleness&ll=3#gentleness"]{ background-color:lightgreen; }

	a[href*="lt=honesty&ll=4#honesty"],
	a[href*="lt=aggressiveness&ll=0#aggressiveness"],
	a[href*="lt=gentleness&ll=4#gentleness"]{ background-color:green; }

