@-moz-document domain("braacket.com") {
/* Braacket.com dark theme
by mawk (mawwwk#1068)
*/

body {
    --main-bg-color: hsl(240, 11%, 13%);
    --panel-color: hsl(240, 11%, 18%);
    --panel-hover-color: hsl(240, 13%, 20%);
    --panel-mid-color: hsl(240, 9%, 25%);
