}

#content_body, .navbar-default, .navbar-default .navbar-collapse, .navbar-default .navbar-form {
    background-color: var(--main-bg-color);
    color: hsl(223, 22%, 83%);    
}

#content_header, #content_body, .my-navsub {
   box-shadow: 0 1px 3px 0 rgba(0, 0, 0, .56),
    0 1px 1px 0 rgba(52, 60, 88, .49),
    0 2px 10px 0 var(--main-bg-color);
}

.navbar-default, .navbar-default .navbar-collapse, .navbar-default .navbar-form, navbar:before {
