}

.panel-footer, .panel.my-panel-collapsed .panel-heading+.panel-body, .panel.my-panel-collapsed .panel-body+.panel-body {
    border-color: var(--panel-border-color);
}

.table-responsive-overflow-right {
    background-image: linear-gradient(90deg, transparent, var(--main-bg-color));
}

/* Alert (i.e. ranking out of date) */
.alert-default {
    background-color: var(--panel-color);
}

/* Dashboard header */
/* Active icon */
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus {
    background-color:hsl(222, 9%, 27%);
    color:var(--text-light-2);
}

/* Inactive dashboard icons */
.navbar-default .navbar-nav>li>a {
    background-color:var(--main-bg-color);
}

/* Dashboard icon hover */
.my-navsub-ul > li {
    background-color:hsl(214, 7%, 22%);
}

.my-navsub-ul > li.active > a {
    border-bottom-color: hsl(215, 0%, 57%);
}

