    color:white;
}
.my-navsub-label {
    color:hsl(0, 0%, 86%);
}

/* Event name in leagues */
.my-panel-mosaic .panel-heading {
    background-color:var(--panel-light-color);

} 
.my-panel-mosaic .panel-heading td > a:not(.btn) {
    background-color:#fff0;
}

