    background-color: hsl(0, 0%, 17%);
}
/* League owner namebadge hover */
a.badge-primary:hover, a.badge-primary:focus, a.badge-primary:active, .panel-default > .panel-heading .badge-primary:hover, .panel-default > .panel-heading .badge-primary:focus, .panel-default > .panel-heading .badge-primary:active {
    background-color: hsl(0, 0%, 8%);
}
/* "Detail" bar and others */
.my-panel-footer-action > a {
    background-color: var(--panel-color);
    color: var(--link-color);
}

a:hover, a:focus {
    color:var(--link-color-hover);
}

a {
    color: var(--link-color)
}

/* Head to head page */
table tr > th, .table>tbody>tr.active>td {
    background-color: var(--panel-color);
}

/* League/ranking hover */
.table-hover>tbody>tr:hover, .table-hover > tbody > tr.active:hover > td {
    background-color: var(--panel-light-color);
}

/* Import league players from tournament - name field */
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: var(--panel-color);
}
/* H2H table cells */
.table>thead:first-child>tr:first-child>th, .table-bordered>thead>tr>th, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>td, .table > thead > tr > th:not([class*="col-"]), .table > tbody > tr > td:not([class*="col-"]), section:not(:last-child) {
    border-color:var(--table-border-color);
}

.alert-success {
    background-color: hsl(120, 17%, 17%) !important;
    color: hsl(102, 25%, 37%);
}
.alert-danger {
    background-color: hsl(355, 14%, 17%) !important;
    color: hsl(0, 28%, 39%);
}

.alert-warning {
    background-color: hsl(57, 20%, 14%) !important;
    color: hsl(56, 25%, 34%);
}

/* Forms, menus, buttons */
.search-box .input-group-addon:first-child, .search-box .form-control, .search-box .form-group.active .input-group-addon, .btn-default {
    background-color: hsl(240, 8%, 18%);
    border-color:var(--table-border-color);
    color:hsl(0, 0%, 82%);
}

/* "Admin" menu for league tournament */
.my-panel-mosaic .panel-heading a:not(.btn) {
    color: var(--text-light);
}
.search-box .form-group.active .form-control:not(:focus), .search-box .input-group-btn .btn, .search-box .btn-group .btn, .search-box .form-control, .input-group-addon+.input-group-addon, .input-group-addon:not(:first-child):not(:last-child), .input-group-btn:not(:first-child):not(:last-child), .input-group .form-control:not(:first-child):not(:last-child), .btn-sm, .btn-group-sm > .btn, .open>.dropdown-toggle.btn-default, .open>.dropdown-toggle.btn-default:hover {
    background-color: hsl(240, 8%, 20%);
    border-color:var(--table-border-color);
    color:var(--text-color-3);
}

/* Dropdown menus */
.dropdown-menu {
    background-color: var(--button-color);
}

.dropdown-menu>li>a, .dropdown-header {
    color:var(--text-light)
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
    background-color:var(--button-light-color);
