}

/* Typeable forms (league settings) */
.input-group-addon, .form-control, .disabled input.form-control, .disabled select.form-control {
    background-color: hsl(240, 8%, 12%);
    color:var(--text-color-3);
}

.search-box .form-group.active .input-group-addon, .search-box .form-group.active .form-control:not(:focus), .search-box .form-group.active .btn {
    border-color: hsl(240, 8%, 29%);
}
/* Buttons */
.btn-group>.btn-group:first-child:not(:last-child)>.btn:last-child, .btn-group>.btn-group:first-child:not(:last-child)>.dropdown-toggle, .btn-group>.btn-group:not(:first-child):not(:last-child)>.btn, .btn-group>.btn-group:last-child:not(:first-child)>.btn:first-child {
