/* Buttons hover */
.btn-group>.btn-group:last-child:not(:first-child)>.btn:first-child.btn:hover,.btn-group>.btn-group:not(:first-child):not(:last-child)>.btn:hover, .btn-group>.btn-group:first-child:not(:last-child)>.btn:last-child:hover, .btn-group-vertical>.btn:hover, .btn-group>.btn:focus, .btn-group-vertical>.btn:focus, .btn-group>.btn:active, .btn-group-vertical>.btn:active, .btn-group>.btn.active, .btn-group-vertical>.btn.active {
