    color: var(--text-light-2);
    border-color: hsl(0, 0%, 36%);
}

.btn-default:hover {
    background-color: var(--button-light-color);
    color: var(--text-light-2);
}

/* Disabled buttons */
.input-group-addon:not(:first-child):not(:last-child) {
    background-color: hsl(240, 8%, 16%);
}
/* League stats chart, imported from google? hard to change eh */
rect {
