data.css:
@-moz-document domain("douban.com") {
html,
body {
    background-color: #333;
}

div,
ul,
li,
span,
p,
blockquote,
figure {
    color: white !important;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #29a19c !important;
}

div.content,
div.cover,
div.block,
#g-side-info .ft,
td.tablelc,
td.tablecc,
td.tablerc,
p:hover {
    background-color: #333 !important;
    color: white !important;
}

.rating-stars {
    color: #e6a157 !important;
}

div.cover:hover {
    background-color: transparent !important;
}


span.react-text,
.action-btn span,
.channel-item .likes {
    color: #0fabbc !important;
}


/*个人信息左右角落圆角*/
div.ex1,
div.ex2 {
    visibility: hidden;
}

/*优选评论背景图*/
li.comment-item h3,
div.meta-header,
div.nlst {
    background-color: #333 !important;
}

a.collect_btn span {
    color: #29a19c !important;
    background: white;
    padding: 2px 2px 7px 5px !important;
}

a.colbutt {
    background: none !important;
}

.infobox .bd {
    padding: 10px;
    background-color: #333;
    border: 1px solid #aaa;
}

.infobox .ex2,
.infobox .ex1 {
    background: none;
}

#db-isay textarea {
    background-color: white !important;
}

#wrapper a,
#wrapper a:link,
#wrapper a:visited,
#wrapper a:active {
    color: #0fabbc !important;
}


.status-item .block {
    background: #333;
}

.mod {
    background: #333;
    padding: 10px;
}


.stream-items .status-wrapper {
    border-bottom: 1px solid #555;
}

.topics .topic-item .abstract {
    color: #ccc;
}

#interest_sectl .rating_num {
    color: rgb(224, 144, 21);
}

.gray_ad {
    background-color: #333;
}

.tag,
.tag:link,
.tag:visited {
    background-color: #333 !important;
}

.tag:hover {
    background-color: white !important;
}

/*豆瓣电影清单*/
.doulist-item {
    border-bottom: 1px dashed #aaa;
}
.doulist-item .mod,
.doulist-item .doulist-subject {
    background-color: #333 !important;
}

#wrapper a:visited,
#wrapper a:active {
    color: white !important;
}

.user-group-list {
    background: white !important;
}
.user-group-list a {
    color: #333 !important;
}

a.bn-big-action,
a.bn-big-action:link,
a.bn-big-action:hover,
a.bn-big-action:visited {
    background: #555 !important;
}

.select .select-item {
    background-color: transparent !important;
}

.dropdown-menu {
    background-color: #333 !important;
}

.picker-wrap {
    color: #333 !important;
    background-color: transparent !important;
}

.topic-reply li {
    background-color: transparent !important;
}

.topic-reply li .reply-doc h4 {
    background-color: #333 !important;
    border-bottom: 1px dashed #aaa;
}

div#top-nav-notimenu * {
    color: #3e3e3e !important;
    background: white !important;
}
}

@-moz-document domain("movie.douban.com") {
.tag-list label {
    color: #f0ad6e !important;
}

.gallery-frame .gallery-detail {
    background: none !important;
}
}

@-moz-document domain("music.douban.com") {
div.inner {
    background: #333 !important;
}

.section-switcher .header {
    background-color: #333 !important;
}

.popular-artists.section .artists,
.popular-artists.section .new-artists {
    background-color: #333 !important;
}
}

@-moz-document domain("web.telegram.org"), domain("web.tlgrm.eu") {
  html body, div .modal-content {
    color: #bbb;
    background: url("https://raw.githubusercontent.com/CaelanBorowiec/Web-Telegram-Responsive-Dark-Purple/master/img/what-the-hex-dark.png")
  }

  a.tg_checkbox {
    color: #bbb !important;
  }

  div.im_page_wrap {
    /*background color of contentwrapper under header*/
    background-color: #303030;
    border-left: 1px solid #4d4d4d;
    border-right: 1px solid #4d4d4d;
    border-bottom: 1px solid #4d4d4d;
    box-shadow: none;
    max-width: 90%;
    position: relative;
  }

  div.im_page_wrap::after {
    content: "";
    background: url(https://shop.mikael-b.com/wp-content/uploads/2017/02/mikaelb-worldmap-art-iceworld-purple.png);
    opacity: 0.5;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
    display: block;
  }

  div.im_page_wrap>div {
    z-index: 1;
    position: relative;
  }

  .im_attach, .im_media_attach, .im_record, .im_record_bg, .im_record:hover, .im_message_document_thumbed, .composer_emoji_panel a.composer_emoji_btn {
    background: inherit !important;
  }

  .im_dialog_peer {
    /*peer nickname unselected*/
    color: #bbb !important;
  }

  .contacts_modal_members_list a.contacts_modal_contact {
    /*nicknames in contactlist via hamburger menu*/
    color: #bbb;
  }

  div.im_dialogs_scrollable_wrap .active a.im_dialog {
    background: linear-gradient(45deg, #90318c, transparent);
    border: 1px solid purple;
  }

  div.tg_head_split, div.md_modal_head {
    /*page header block*/
    background-color: purple;
  }

  .dropdown.open .tg_head_btn, .tg_head_btn:hover {
    background: #4d4d4d !important;
  }

  .form-control {
    /*input field*/
    background-color: #0e0e0e !important;
    color: #bbb !important;
  }

  .composer_rich_textarea {
    background: #303030 !important;
    border-color: #bbb !important;
  }

  .im_dialogs_col_wrap {
    /*contactlist right border*/
    border-right: 2px solid #4d4d4d;
    display: block;
    position: relative;
  }

  .im_dialogs_col_wrap::after {
    content: "";
    background: purple;
    opacity: 0.2;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
    z-index: -1;
  }

  .im_dialogs_col .nano>.nano-pane>.nano-slider {
    /*scrollbarbutton when hover*/
    background-color: #4d4d4d !important;
  }

  code, pre {
    background-color: #bbb !important;
    color: #0e0e0e;
    border: none !important;
  }

  div.tg_head_split {
    max-width: 90%;
  }

  li.im_dialog_wrap {
    background: linear-gradient(45deg, black, transparent);
  }

  .im_submit {
    color: #4d4d4d;
  }

  div.im_send_panel_wrap {
    background: linear-gradient(0deg, #5f004f, #0000001c);
    border-radius: 15px 15px 0px 0px;
    padding: 15px 49px 30px;
    max-width: 650px;
    border: 1px solid purple;
    border-bottom: none;
  }

  .im_message_mymention {
    background: #1d1d1d;
  }

  .im_message_focus .im_message_outer_wrap {
    background-color: #4d4d4d;
  }

  .im_message_selected .im_message_outer_wrap {
    background: #4d4d4d;
  }

  div.im_grouped_short {
    margin-top: -15px;
  }

  .im_grouped_short .im_message_body {
    margin: 0;
  }

  .im_history_message_wrap {
    margin: 0 auto;
    max-width: 50%;
  }

  div.im_content_message_wrap {
    padding: 15px;
    border: 1px solid purple;
    background: rgba(0, 0, 0, .5);
    border-radius: 15px;
  }

  div.im_message_wrap {
    max-width: 75%;
    padding: 0 15px 0 15px;
    display: inline-block;
  }

  a:hover {
    background-color: #282828 !important;
  }

  .im_message_unread_split {
    background: linear-gradient(90deg, transparent, black, transparent);
    border-top: 1px solid purple;
    border-bottom: 1px solid purple;
  }

  @keyframes im_message_focus_fade {
    0% {
      /*soft transaction*/
      background-color: #2d2d2d;
    }
  }

  .btn-primary {
    background-color: #4d4d4d;
  }

  .im_history_select_active .im_message_outer_wrap:hover {
    background: #4d4d4d;
  }

  .im_dialog_chat_from_wrap, .im_dialog_message_service {
    color: #6ac;
  }

  .composer_rich_textarea, .composer_textarea {
    box-shadow: 0px 3px 1px #ddd;
  }

  .composer_rich_textarea:focus {
    box-shadow: 0px 3px 1px #ddd !important;
  }

  .tg_head_logo_dropdown .dropdown-menu>li>a {
    color: #ddd;
  }

  .btn-primary.disabled, .btn-primary[disabled] {
    background: #4d4d4d none repeat scroll 0% 0%;
  }

  /*emoji*/
  .composer_emoji_tooltip, .icon-tooltip-tail, .dropdown-menu {
    background-color: #4d4d4d !important;
    border-color: #5d5d5d;
  }

  div .im_dialog_badge {
    background-color: purple;
  }

  div .im_dialog_badge_muted {
    background: #555;
  }

  .im_message_selected .im_message_outer_wrap, .im_history_select_active .im_message_outer_wrap:hover {
    background: linear-gradient(90deg, transparent, #800080e6, transparent);
  }

  .reply_markup_button {
    color: #FFFFFF !important;
    background-color: #666666 !important;
  }

  .reply_markup_button:focus, .reply_markup_button:hover {
    color: #FFFFFF !important;
    background-color: #4d4d4d !important;
  }

  .tg_head_logo_dropdown .dropdown-menu>li>a, .dropdown-menu>li>a, .dropdown-menu>li>a, .dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
    color: #FFFFFF !important;
  }

  @media (max-width: 1000px) {
    .im_message_wrap {
      position: relative;
      max-width: 100% !important;
    }

    .im_history_message_wrap {
      max-width: 100%;
    }
  }
}

/*
    
    Theme Name: Sankaku Complex Dark Surface

    Theme By: Adam-J-Frost

    Updated: 2020/01/06

    Created: 2017/09/08
    
    Description: This is a Dark Style for Sankaku Complex.

*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://chan.sankakucomplexxx.com/"), domain("chan.sankakucomplex.com"), domain("idol.sankakucomplex.com") {
:root {
    --header-img: url("https://image.ibb.co/meUKWa/sankaku_complex_logo_blue.png");
    --stars: url("https://image.ibb.co/fVatra/hYnBSTn.png");
    --stars-fill: url("https://image.ibb.co/hz73Pv/star1.png");
    --crown: url("https://image.ibb.co/meUKWa/sankaku_complex_logo_blue.png");
    --transition-speed-a: 0.2s;
    --transition-speed-b: 0.5s;
    --background-color: #0d0d0d;
    --content-box-color: #1a1a1a;
    --content-box-color-2: #1e1e1e;
    --sb-color: #1a1a1a;
    --sb-border-color: #2e2e2e;
    --sb-border-focus-color: #4285f4;
    --border-color: #2e2e2e;
    --border-hover-color: #2e2e2e;
    --border-radius: 2px;
    --font-color: #fff;
    --font-a-color: #4285f4;
    --font-hover-color: #8cb4f8;
    --btn-color: #1e1e1e;
    --btn-hover-color: #3a3a3a;
    --btn-border-color: #2e2e2e;
    --highlight-color: #4a4a4a;
    --selection-color: #3a3a3a;
}

/* Block Ads Start */
cloudflare-app[app-id="no-adblock"],
div.scad,
#acb6e325,
div.scad-i {
    display: none;
}

/* Block Ads End */
body,
body.en,
body.ja,
div#header {
    background: var(--background-color);
    color: var(--font-color);
}
div#news-ticker {
    background: var(--content-box-color);
    border-bottom: 2px solid var(--border-color);
}
a,
h1,
h2,
h3,
h4,
h5,
h6,
div#header ul#navbar li.lang-select,
.no-comments {
    color: var(--font-color);
    -webkit-transition: var(--transition-speed-a) ease-out;
    -moz-transition: var(--transition-speed-a) ease-out;
    -o-transition: var(--transition-speed-a) ease-out;
    transition: var(--transition-speed-a) ease-out;
}
a:link,
a:visited {
    color: var(--font-a-color);
    -webkit-transition: var(--transition-speed-a) ease-out;
    -moz-transition: var(--transition-speed-a) ease-out;
    -o-transition: var(--transition-speed-a) ease-out;
    transition: var(--transition-speed-a) ease-out;
}
a:link:hover {
    color: var(--font-color);
    -webkit-transition: var(--transition-speed-a) ease-out;
    -moz-transition: var(--transition-speed-a) ease-out;
    -o-transition: var(--transition-speed-a) ease-out;
    transition: var(--transition-speed-a) ease-out;
}
div#header ul#subnavbar {
    background-color: var(--content-box-color);
    border: 1px solid var(--border-color);
}
div#header ul#navbar li.current-page {
    background-color: var(--content-box-color);
    border: 1px solid var(--border-color);
    border-bottom: 2px solid var(--border-color);
}
div#headerlogo img[class=""] {
    background: var(--header-img) no-repeat !important;
    height: 0 !important;
    width: 0 !important;
    padding-left: 392px !important;
    padding-top: 100px !important;
}
div#header ul#navbar li {
    border: 1px solid var(--border-color);
    border-bottom: none;
}
div#header ul#navbar li:hover > ul {
    background: var(--content-box-color);
}
div#header ul#navbar li:hover ul li:hover {
    border: 1px solid var(--border-color);
    border-bottom: 2px solid var(--border-color);
}
div#header ul#navbar li:hover {
    background-color: var(--btn-hover-color);
    border: 1px solid var(--border-color);
    border-bottom: 2px solid var(--border-color);
}
div.sidebar {
    border: 1px solid var(--border-color);
}
#popular-preview {
    border: 1px solid var(--border-color);
    margin: 0 1em 2em 1em;
    text-align: center;
    background: var(--crown) center 0.5em no-repeat;
}
div.status-notice {
    background: var(--content-box-color);
    border: 1px solid var(--border-color);
}
.unit-rating {
    background: var(--stars) left center repeat-x;
    list-style: none;
    margin: 0px;
    padding: 0px;
    width: 170px;
    height: 34px;
    position: relative;
    -webkit-transition: none;
    -moz-transition: none;
    -o-transition: none;
    transition: none;
}
.unit-rating li {
    background: var(--stars-fill) left top;
    height: 34px;
    display: block;
    text-indent: -9000px;
    z-index: 1;
    -webkit-transition: none;
    -moz-transition: none;
    -o-transition: none;
    transition: none;
}
.unit-rating li a {
    -webkit-transition: none;
    -moz-transition: none;
    -o-transition: none;
    transition: none;
}
.unit-rating li a:hover {
    background: var(--stars-fill) left bottom;
    z-index: 2;
    left: 0px;
    -webkit-transition: none;
    -moz-transition: none;
    -o-transition: none;
    transition: none;
}
#add-to-favs,
#remove-from-favs {
    background-image: none;
    background-repeat: no-repeat;
}
div.status-notice.pending {
    background: var(--content-box-color-2);
    border: 2px solid var(--border-color);
}
#recommendations-paginator > span.recommended-prev,
#recommendations-paginator > span.recommended-next {
    border: 1px solid var(--border-color);
    color: var(--font-color);
}
input#tags {
    background: var(--sb-color);
    border: 1px solid var(--sb-border-color);
    border-radius: var(--border-radius);
    border-color: var(--sb-border-color);
    color: var(--font-color);
    -webkit-transition: var(--transition-speed-b) ease-out;
    -moz-transition: var(--transition-speed-b) ease-out;
    -o-transition: var(--transition-speed-b) ease-out;
    transition: var(--transition-speed-b) ease-out;
}
input#tags:hover {
    background: var(--sb-color);
    border: 1px solid var(--sb-border-focus-color);
    -webkit-transition: var(--transition-speed-b) ease-out;
    -moz-transition: var(--transition-speed-b) ease-out;
    -o-transition: var(--transition-speed-b) ease-out;
    transition: var(--transition-speed-b) ease-out;
    box-shadow: 0 0 0 0 transparent;
}
input#tags:focus {
    background: var(--sb-color);
    border: 1px solid var(--sb-border-focus-color);
    box-shadow: 0 0 0 0 transparent;
}
input[type="text"]:focus {
    background: var(--sb-color);
}
div#post-content {
    padding-top: 0;
}
div.response-list > div.comment {
    border-top: 2px solid var(--border-color);
}
#popular-preview {
    border: 1px solid var(--border-color);
    margin: 0 1em 2em 1em;
    text-align: center;
    background: url("https://image.ibb.co/iQSuVc/crown.png") center 0.5em no-repeat;
}
body.no-scroll {
    overflow: visible !important;
    position: relative !important;
}
#sank-prestitial {
    display: none !important;
}

/* Default Tag Colors Start */
.tag-type-medium > a {
    color: #FF761C;
}
.tag-type-artist > a {
    color: #A00;
}
.tag-type-artist > a:hover {
    color: #C66666;
}
.tag-type-idol > a {
    color: #FF0066;
}
.tag-type-copyright > a {
    color: #A0A;
}
.tag-type-character > a {
    color: #0A0;
}
.tag-type-studio > a {
    color: #FF2DCD;
}
.tag-type-meta > a {
    color: #782dff;
}

/* Default Tag Colors End */
}

/*
    
    Theme Name: How-To-Type Dark Surface

    Creator: Adam-J-Frost.
    
    Updated: 2020/01/06

    Created: 2018/07/30
    
    Description:

    This is a Dark Style for How-To-Type.com.

    Hope you like it and thanks for using it!

*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("how-to-type.com") {
body {
    background: #0d0d0d;
}
a:focus,
a:hover {
    color: #007fff;
}
#header {
    background: #0d0d0d;
    background-image: none;
    border-bottom: 1px solid #2e2e2e;
}
#features {
    background-color: #1e1e1e;
    font-family: Courier New, Arial, Helvetica, sans-serif;
    padding-bottom: 2em;
}
#splash {
    background: #1e1e1e;
    height: 150px;
    padding-top: 40px;
}
#splash h1 {
    text-shadow: 0 0 0;
}
#logo a {
    color: #ffffff;
}
#logo a:hover {
    color: #007fff;
}
#logo h1 {
    color: #fff;
    text-shadow: 0 0 0;
}
#logo h2 {
    color: #ffffff;
}
.lesson {
    background: #1a1a1a;
    color: #fff;
    box-shadow: 0 0 0;
}
.lesson a {
    color: #007fff;
}
.quote {
    font-family: Courier;
    background: #1a1a1a linear-gradient(#3a3a3a, #2a2a2a);
    border: 1px solid #2e2e2e;
    border-radius: 0;
}
div.result {
    background: #1a1a1a linear-gradient(#3a3a3a, #2a2a2a);
    border: 1px solid #2e2e2e;
    border-radius: 0;
}
.cursor {
    background: #007fff;
}
.correct {
    color: #007fff;
}
.large.stat,
.large.stat .bottom,
.large.stat.errors .details {
    color: #007fff;
}
.button {
    background: #1a1a1a;
    border: 1px solid #2e2e2e;
    color: #ffffff;
    border-radius: 2px;
    box-shadow: 0 0 0;
}
.button:hover {
    background: #2a2a2a;
    border: 1px solid #2e2e2e;
    color: #ffffff;
    border-radius: 2px;
    box-shadow: 0 0 0;
}
a:hover .button {
    background: #007fff;
    color: #ffffff;
    text-decoration: none;
    transform: scale(1.1);
    box-shadow: 0 0px 15px #007fff;
}
.lesson-list ul a {
    background: #1a1a1a;
    color: #ffffff;
}
.lesson-list a:hover {
    background: #007fff;
    color: #ffffff;
    box-shadow: 0 0px 10px #007fff;
}
.lesson-list a {
    background: #007fff;
    color: #fff;
}
.choice {
    background-color: #1a1a1a;
    border: 1px solid #2e2e2e;
    border-radius: 2px;
    color: #ffffff;
    padding: 5px;
    margin: 20px;
    margin-right: 20px;
    margin-bottom: 20px;
    clear: both;
    float: right;
}
#footer {
    background-image: none;
    height: 75px !important;
}
div#wrapper {
    padding-bottom: 75px;
    margin-bottom: 50px;
}
#footer p {
    margin: 0;
    padding: 3px;
    padding-right: 5em;
    line-height: normal;
    font-size: 10px;
    text-transform: lowercase;
    text-align: center;
}
#octopus_side_img {
    display: none;
}
}

@-moz-document regexp("^(https://)?\\w*.?klp.pl/(.*)?") {
/* latin-ext */
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v20/KFOmCnqEu92Fr1Mu7GxKOzY.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Roboto';
  font-style: normal;
