}
.jwlistV2 .jwrowV2.playing:hover,
.jwlistV2 .jwrowV2:hover,
.jwrow:hover
{
    Background-color: #191919 !important;
    transition: all 0.4s ease-in-out !important;
}
.jwlistV2 .jwrowV2,
.s2xx.snapshot-link,
.s2xx.snapshot-link:hover
{
    transition: all 0.4s ease-in-out !important;
}
.jwlist div[class~="jwrow"][class~="playing"]
{
    background: #222 !important;
    font-weight: bold;
    transition: all 0.4s ease-in-out;
}


/* Calendar Page - Feedback-button, Top Right & RSS-Button */
.btn-primary,
.label-success,
.label-success,
.btn-success,
.iconochive-download,
.boxy-ttl.hover-badge
{
    Background-color: #222 !important;
    border-color: #444;
    transition: all 0.4s ease-in-out !important;
}

.btn-primary:hover,
.beta_button:hover,
.label-success:hover,
.btn-success:hover,
.iconochive-download:hover,
.boxy-ttl.hover-badge:hover,
.item-download-options .show-all:hover .hover-badge:hover,
.ui-menu .ui-menu-item-wrapper:hover
{
    Background: #444 !important;
    Background-color: #444 !important;
    border-color: #444!important;
}


/*Donate Banner*/
#donate_banner,
#donate_banner .donate-body,
#donate_banner .donate-form div.donate-form-section li label, #donate_banner .donate-form div.donate-form-section li input,
#donate_banner .donate-form div.donate-form-section input[type="radio"] + label, #donate_banner .donate-form div.donate-form-section input[type="radio"] + .blue-bg
{

    background: #111!important;
    border-color: #333;
    text-shadow: none !important;
}

#donate_banner .donate-body .highlight
{
    background-color:#0000;
    color:var(--ArchiveLinksHover);
}
#donate_banner .donate-form div.donate-form-section fieldset legend
{
    color:#aaa;
}

#donate_banner .donate-close
{
    transition:.4s;
}

#donate_banner .donate-close:hover
{
    color:#555;
