	background: url(https://i.ibb.co/fvPrbJ4/whatsapp.png) no-repeat!important;
	box-sizing: border-box;
}

.shareButtons-button.shareButtons-button--whatsApp>i:before {
	background-size: 31px!important;
	padding-left: 33px;
}

/* YouTube _____________*/

a.shareButtons-button.shareButtons-button--youtube>i:before {
	background: url(https://i.ibb.co/8cHpnPV/You-Tube-logo-play-icon-30x23.png) no-repeat!important;
	box-sizing: border-box!important;
}

a.shareButtons-button.shareButtons-button--youtube>i:before {
	background-size: 26px!important;
	padding-left: 27px!important;
}
}

@-moz-document url-prefix("https://www6.dramanice.movie/") {
a {color: white !important;}
body { background: black; color: white;}
.header .banner_menu {background-color: black;}
.anime_muti_link ul li:nth-child(2n+1) {background-color: black;}
body > footer > div { background: black; color: white;}
#keyword {width: 100%}

.menu_top ul li {background: none;}
.menu_top ul {font-family: monospace;}

footer .inner {background: #000000;}

.commingbox h3.title {color: #ffffff;}

.content_episode_popular_slide {
    background-color: #000000;
}

ul.items li .img {
    border-radius: 0%;
}
}

@-moz-document domain("tvtime.com") {
*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #1e1e1e ! important;
}
*::-webkit-scrollbar-button {
    display: none ! important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #3b3b3b ! important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #1c1c1c ! important;
}
#get-the-app {
    display: none;
}
body {
    color: #c6c6c6;
}
.body-inner {
    background-color: #272727;
}
.page-center,
#profile,
html,
body.white {
    background-color: #222;
}
.fanarts-list > li.watched .fanart-container {
    opacity: .5;
}
.sidebar-link.left-sidebar-link {
    color: #3b3b3b;
    right: 15px;
}
.fanarts-list > li .fanart-container,
#profile-badges .badges-group,
#profile-leaderboards,
#profile-stats {
    background-color: #1c1c1c;
    border-color: #3b3b3b;
}
.fanarts-list > li .fanart-details p {
    border-color: #3b3b3b;
    color: #c6c6c6;
}
.fanarts-list > li .fanart-details h2 a,
.fanarts-list > li .fanart-details .secondary-link,
#calendar .day .day-header,
#show-details .basic-infos,
.article-comments--count,
.posters-list > li .poster-details h2 a,
#explore section h1,
h2.title,
section .title,
.page-column section h1,
#episode-details .episode-infos .basic-infos,
#actor-details .actor-header .infos,
#actor-details .role .role-infos a {
    color: #c6c6c6;
}
#calendar .day,
#calendar .day .day-header .relative-date,
#content .content-container .right section,
#show-leaderboard ul li,
.comment .post .options,
.comment .reply:not(form),
#calendar-picker,
#profile .stats-list .stat-item,
#episode-details #content .not-watched-episode .stats-list .stat-item,
#episode-details #content .not-watched-episode .not-watched-infos,
#actor-details .actor-header {
    border-color: #3b3b3b;
}
#calendar .day.today .day-header .day-info .day-number,
#calendar .day.today .day-header .day-info .month-label,
.comment .post > .top .holder .author-label,
.comment .post > .top .holder .author-label a,
#profile .profile-nav .nav-tabs > li.active .count,
#profile .profile-nav .nav-tabs > li.active .label {
    color: #f5d248;
}
#calendar .day.today .day-header .relative-date,
#calendar .day.tomorrow .day-header .relative-date,
#calendar .day.yesterday .day-header .relative-date,
.posters-list > li .poster-details .secondary-link {
    color: #949494!important;
}
.page-right,
#content .content-container .right {
    border-color: #3b3b3b;
    background-color: #272727;
}
#show-details.show .show-nav,
#episode-details .episode-infos,
#episode-details .episode-infos.extended,
#actor-details .actor-header .infos,
.leaderboard-ad {
    background-color: #333333;
    border-color: #272727;
}
#show-details .seasons .episode-wrapper .infos a,
#show-details .seasons .episode-wrapper .infos:hover i.icon-play-circle,
.comment .reply .author-label a,
#profile h2.small,
#profile .profile-nav .nav-tabs > li .label {
    color: #ccc;
}
#show-details .seasons .episode-wrapper.odd {
    background-color: #353535;
}
#show-details .seasons .episode-wrapper.current {
    background-color: #FFE25F;
}
#show-details .seasons .episode-wrapper.future,
#show-details .seasons .episode-wrapper.future a,
.comment .post > .top .holder blockquote p,
.comment .reply > .holder blockquote p,
.cast .actor-item .content h3,
#profile-badges ul .badge-item.unlocked .badge-title,
#profile .profile-nav .nav-tabs > li:hover .label {
    color: #fff;
}
#show-details .seasons .episode-wrapper.current,
#show-details .seasons .episode-wrapper.current a,
#show-details .seasons .episode-wrapper.current span.episode-air-date,
.cast.cast-ranking .actor-item .rank-sticker,
#calendar-picker table tr td.today.day.has-episodes:after,
#calendar-picker table tr td.active.day.has-episodes:after {
    color: #222;
}
.comment:not(.new-comment) .post {
    background: #272727;
    border-color: #333;
}
.comment .sub-comment,
#notifications ul li {
    background: #313131;
    border-color: #333;
}
.article.article--display .half-divider {
    background-color: #3b3b3b;
}
#friend-requests,
#notifications,
#putio-zone,
#referrals,
#actor-details .role {
    background-color: #272727;
    box-shadow: 0 0 1px 1px #333;
}
#friend-requests ul:after,
#notifications ul:after,
#putio-zone ul:after,
#referrals:after,
header #global-search-results ul:after {
    border-color: transparent transparent #333;
}
.highcharts-series.highcharts-tracker path {
    stroke: #3b3b3b;
}
#profile .profile-nav {
    background-color: #3b3b3b;
    border-color: #333333;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover,
.nav > li > a:focus,
.nav > li > a:hover {
    background-color: #3b3b3b;
}
#profile .profile-nav .nav-tabs > li.active,
#profile .profile-nav .nav-tabs > li:focus {
    border-color: #f5d248;
}
#calendar .day .day-header .day-info .day-label {
    font-size: 17px;
    line-height: 26px;
}
#calendar .day .day-header .day-info .day-number {
    font-size: 65px;
    line-height: 70px;
}
#calendar .day .day-header .day-info .month-label {
    font-size: 15px;
}
#calendar .day .day-header .relative-date {
    font-size: 14px;
}
#calendar .fanarts-list > li {
    width: 180px;
}
#calendar .fanarts-list > li .fanart-container {
    width: 180px;
    height: auto;
}
#calendar .fanarts-list > li .image-crop {
    width: 100%;
    height: 100px;
}
#calendar .fanarts-list > li .fanart-details p,
#calendar .fanarts-list > li .fanart-details .more-infos {
    width: 178px;
}
#calendar .day .day-header .relative-date {
    padding-top: 5px;
    margin: 5px auto 0;
}
.fanarts-list > li .fanart-details .more-infos {
    height: auto;
}
body.reduced-left:not(.not-connected) .page-left section {
    border-top-width: 0;
}
.loader.dark {
    border-color: #5d5d5d;
}
#genre-chart {
    fill: #cccccc;
}
#genre-chart g.highcharts-legend text {
    color: #cccccc!important;
}
#genre-chart g.highcharts-legend text:hover {
    fill: #fff!important;
}
}
