@-moz-document url-prefix("https://realkana.com") {
/* Made by s0m3guy */

body, input {
    color: #fff;
}

div.all {
    background-image: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/background_body.jpg?raw=true")
}

.all__content, .link--button-white, .study__right, .study__shown {
    background-image: none;
    background-color: #0E0E1080;
    box-shadow: none;
}

h1, .tabs__link--front:visited {
    color:#1285EC;
}

.tabs__tab {
    background: #1C1C1E80;
    border-color: #ffffff50;
    border-width: 0 0 1px 1px
}
.tabs__link {
    box-shadow:none
}

/* Intro */

.top--text {
    background: none
}
h1+h2, .options__label, .options__label:hover {
    color: #a2c8f4
}

/* Hiragana and Katakana */

.link--plain {
    color: #fff;
    border-bottom: 1px dotted #ccc
}

.link--plain:hover {
    color: #a2c8f4;
    border-bottom: 1px solid #ccc
}

.kana__cell {
    border-color: #ffffff50;
}

.checkboxes__cell {
    border-right: 1px solid #ffffff50
}

.kana__character, .preview__icon, .study__question-kana {
    background-image: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/kana.png?raw=true")
}
.kana, .preview, .typefaces {
    background-color: #E3E3E120
}

/* Study */
.study__circle {
    background-image:none;
}
.study__input, .study__input--blurred {
    background-color: #0E0E1080;
    border: 1px solid #ffffff50;
    box-shadow: none     
}

/* Extra */

.typefaces__label {
    background: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/typefaces.png?raw=true") no-repeat;
    background-size: 562px 47px;
}

.typefaces__label--1 {
    background-position: 0 14px;
}

.typefaces__label--2 {
    background-position: -62px 14px;
}

.typefaces__label--3 {
    background-position: -124px 14px
}

.typefaces__label--4 {
    background-position: -186px 14px;
}

.typefaces__label--5 {
    background-position: -248px 14px;
}

.typefaces__label--6 {
    background-position: -310px 14px;
}

.typefaces__label--7 {
    background-position: -372px 14px;
}

.typefaces__label--8 {
    background-position: -434px 14px;
}

.typefaces__label--9 {
    background-position: -496px 14px;
}

.options {
    background:none
}

.message {
    background-color: #E3E3E120
}

/* App */

.device__frame {
    background: #000;
    padding: 40px 0 40px;
    border: 2px solid #ffffff50;
    border-radius: 30px;
    box-shadow: none
}

.device__image {
    background-image: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/device.jpg?raw=true");
    border: 5px solid #000
}

.device__stars {
    display: none
}
}

@-moz-document url-prefix("https://www.brick-hill.com/")
{
  *
  {
    font-family: Arial, Helvetica, sans-serif;
    box-shadow: unset;
  }

  element.style
  {
    border-radius: 5px;
  }

  body,
html
  {
    background-image: url('http://web.archive.org/web/20170330222627im_/http://beta.brick-hill.com/assets/bkg.png');
    background-position: center top;
    margin: auto;
    margin-top: auto;
    margin-top: 10px;
    width: 1050px;
  }

  nav li
  {
    bottom: 20px;
  }

  nav .primary li
  {
    bottom: 20px;
    font-weight: 700;
    font-size: 16px;
  }

  nav li a:not(.active):hover
  {
    text-decoration: underline;
    border-color: transparent;
  }

  nav li a
  {
    padding: 8px 5px;
    padding-right: 0;
    padding-left: 0;
  }

  nav li:first-child
  {
    padding-left: 10px;
  }

  nav
  {
    width: 900px;
    margin: auto;
    margin-left: auto;
    margin-right: auto;
    margin-top: 50px;
    margin-top: 0px;
  }

  .primary,
.secondary
  {
    background-color: transparent;
  }

  nav div.primary
  {
    background-color: transparent;
    background-image: url('http://web.archive.org/web/20181101202744im_/https://www.brick-hill.com/assets/Banner.png');
    background-size: 100%;
    padding-top: 18%;
    overflow: hidden;
    background-repeat: no-repeat;
  }

  nav div.primary div.grid
  {
    background-color: #77B9FF;
    margin-top: 1px;
  }

  nav div.primary div.grid,
nav div.secondary div.grid
  {
    border: 1px solid #000;
    border-top: none;
  }

  .bottom-bar
  {
    color: white;
    text-align: center;
    border-top: none;
    background-color: #99CBFF;
    line-height: 0.7;
    font-size: 16px;
    margin-top: -5px !important;
  }

  .secondary .grid
  {
    padding-top: 5px;
  }

  .primary #info
  {
    float: none;
    line-height: 20px;
  }

  nav .info
  {
    border-radius: 0;
    padding: 5px 10px 15px;
    background-color: #77B9FF;
    display: inline-block;
    border: 1px solid #000;
    width: 179px;
    top: 70px;
    position: absolute;
    right: 80px;
  }

  div.info div:nth-of-type(3n)
  {
    float: left;
  }

  div.info div:nth-of-type(4n)
  {
    float: right;
    margin-right: 16px;
  }

  .card
  {
    border: 1px solid #000;
    border-radius: 0;
    -webkit-box-shadow: unset;
    box-shadow: unset;
  }

  .input-group.fill input
  {
    overflow: initial !important;
  }

  .card .content
  {
    padding: 6px;
    border-radius: 0 !important;
    box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
    background-color: #E9EBF3;
    overflow: auto;
    height: 100%;
    box-sizing: border-box;
  }

  .card .top:not(.absolute).blue,
.card .top:not(.absolute).red,
.card .top:not(.absolute).orange,
.card .top:not(.absolute).green,
.card .top:not(.absolute)
  {
    background-color: #E9EBF3;
    color: #333;
    border-bottom: none;
    border-bottom: 1px solid !important;
    border-color: #aaa !important;
    font-weight: 700;
  }

  .card .top:not(.absolute)
  {
    padding: 5px;
  }

  .card .top
  {
    padding: 6px;
    box-sizing: border-box;
    background-color: #E9EBF3;
    border-radius: unset;
    border-bottom: 1.5px solid gray;
    box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
    color: #999;
    font-weight: 700;
    line-height: 1.163;
    font-size: 24px;
  }

  .username
  {
    float: left;
    margin: 5px 20px 20px 20px;
    color: white;
    text-align: left;
    font-size: 16px;
    font-weight: 600;
    background: #77b9ff;
    border: 1px solid black;
    padding: 2px;
    padding-left: 5px;
    padding-right: 5px;
    position: absolute;
    top: 55px;
    left: 7%;
  }

  .primary ul li:after
  {
    content: "|";
  }

  .primary ul li:last-child:after
  {
    content: none;
  }

  .secondary ul li:after
  {
    content: "  |  ";
  }

  .secondary ul li:last-child:after
  {
    content: none;
  }

  .card.forum-links
  {
    background-color: #77B9FF;
  }

  .forum-options a,
.forumPage,
.forumPage.blue
  {
    color: #fff;
  }

  .card.forum-links .content
  {
    background: #77b9ff;
  }

  .forum-links a
  {
    color: white;
  }

  footer
  {
    background: #00508a;
  }

  .main-holder.grid
  {
    width: 95%;
    display: block;
    margin-top: 10px;
    margin-left: auto;
    margin-right: auto;
  }

  .bits-icon
  {
    background: url('https://i.imgur.com/XdwCtln.png');
    background-repeat: no-repeat;
    background-size: auto auto;
    background-position: 0 -22px;
    height: 16px;
    color: Orange;
  }

  .bucks-icon
  {
    background: url('https://i.imgur.com/XdwCtln.png');
    background-repeat: no-repeat;
    background-size: auto auto;
    background-position: 0 -43.5px;
    height: 16px;
    color: Green;
  }

  .friends-icon
  {
    background: url('https://i.imgur.com/XdwCtln.png');
    background-repeat: no-repeat;
    background-size: auto auto;
    height: 16px;
    background-position: 0 -63px;
  }

  .messages-icon
  {
    background: url('https://i.imgur.com/XdwCtln.png');
    background-repeat: no-repeat;
    background-size: auto auto;
    height: 16px;
    background-position: 0 -82px;
  }

  .splash
  {
    margin-top: -20%;
  }

  .button.green,
.forum-button.green,
.forum-create-button.green,
button.green,
input[type=submit].green
  {
    background-color: #03c303;
    border-color: #000000;
    font-size: 12px !important;
    border-radius: 0px;
    padding: 3px;
    font-family: Arial, Helvetica, sans-serif;
  }

  .button, button, .button.blue,
.forum-button.blue,
.forum-create-button.blue,
button.blue,
input[type=submit].blue
  {
    background-color: #77B9FF;
    border-color: #000000;
    font-size: 12px;
    border-radius: 0px;
    padding: 3px;
    font-family: Arial, Helvetica, sans-serif;
  }

  .button.red,
.forum-button.red,
.forum-create-button.red,
button.red,
input[type=submit].red
  {
    background-color: #fe5746;
    border-color: #000000;
    font-size: 12px;
    border-radius: 0px;
    padding: 3px;
    font-family: Arial, Helvetica, sans-serif;
  }

  .button.orange,
.forum-button.orange,
.forum-create-button.orange,
button.orange,
input[type=submit].orange
  {
    background-color: #fdbc38;
    border-color: #000000;
    font-size: 12px;
    border-radius: 0px;
    padding: 3px;
    font-family: Arial, Helvetica, sans-serif;
  }

  div.col-1-1.tab-buttons > button.tab-button.blue
  {
    padding: 12px;
    font-size: 15px;
  }

  .purchase-bucks
  {
    font-size: 15px;
    background-color: #03c303;
    border: 1px solid black!important;
    min-width: 100px;
    border-width: 0;
    cursor: pointer;
  }

  .purchase-bits
  {
    font-size: 15px;
    background-color: var(--price-bits-hex);
    ;
    border: 1px solid black!important;
    min-width: 100px;
    border-width: 0;
    cursor: pointer;
  }

  .tabs .tab
  {
    color: #000;
    border-radius: 0;
  }

  .tabs .tab-body
  {
    border: 1px solid #000;
  }

  .special-icon
  {
    vertical-align: top;
    width: 60px;
    height: 60px;
    background: url('https://i.imgur.com/KOGP3Gs.png');
    background-repeat: no-repeat;
    background-size: auto auto;
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
  }

  .special-e-icon
  {
    vertical-align: top;
    width: 60px;
    height: 60px;
    background: url('https://i.imgur.com/33U2Jaj.png');
    background-repeat: no-repeat;
    background-size: auto auto;
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
  }

  .membership-cubes,
.mem-bottom,
.mem-top
  {
    margin-top: 2px;
    display: none;
  }

  .forum-bar a
  {
    color: #fff;
  }

  .crate-types .active,
.crate-types li
  {
    background-color: #f8f8f8;
    border: 1px solid #000000;
    border-radius: 0px;
    background-color: #77B9FF;
    border-top: none;
    text-align: center;
    color: #fff;
    padding: 7px;
  }

  .crate-types li:hover
  {
    background-color: #77B9FF;
    border-left: 1px solid #000000;
  }

  .crate-types li:first-child
  {
    border-top: 1px solid #000000;
  }

  .crate-types li:first-child,
.crate-types li:last-child
  {
    border-radius: 0;
  }

  .crate-types li:hover
  {
    text-decoration: underline;
  }

  .ellipsis
  {
    color: black !important;
  }

  .profile-card.crate img
  {
    border-radius: 0;
    border: 1px solid #000000;
    background-color: #fff;
  }

  .profile-card.user
  {
    float: left;
  }

  div.profile-card.user.friend > a.forum-create-button.green
  {
    left: 20px !important;
  }

  div.profile-card.user.friend > a.forum-create-button.red
  {
    right: 20px !important;
  }

  button.no-left-rad.f-right.light-border.w600
  {
    background-color: #77B9FF;
    color: white !important;
    border-radius: 0px;
    border: 1px solid #000;
  }

  textarea.wi100.block
  {
    border-radius: 0px;
    border: 1px solid #888;
  }

  input.no-right-rad.f-left.wi100.no-r-border
  {
    border-radius: 0px;
    border: 1px solid #000;
    border-right: none;
  }

  .more-popover.user ul
  {
    box-shadow: none;
  }

  .dropdown-content ul
  {
    background-color: transparent;
    padding-right: 0;
    font-weight: 700;
    font-size: 16px;
    -webkit-box-shadow: none;
    box-shadow: none;
  }

  .more-popover.user li
  {
    padding: 0;
    min-width: 100%;
  }

  .dropdown-content
  {
    visibility: visible;
    opacity: 1;
    top: 175px !important;
    left: 900px !important;
  }

  .dropdown-content:not(.logout-dropdown)
  {
    opacity: 0;
  }

  .dropdown-content ul :hover
  {
    background-color: transparent;
    text-decoration: underline;
  }

  #username-bar
  {
    pointer-events: none;
  }

  .dropdown-arrow
  {
    display: none;
  }

  .col-3-12 .stat::after
  {
    border-left: 1px solid #000;
  }

  div #bucks-amount
  {
    margin-top: 10px;
    float: left;
  }

  div #bits-amount
  {
    margin-top: 10px;
    float: right;
    margin-right: 8px;
  }

  span.friends-icon.img-white,
span.messages-icon.img-white
  {
    filter: invert(0) !important;
  }

  .shop-body
  {
    border: 1px solid #000;
    padding: 20px;
    background-color: #E9EBF3;
  }

  div.col-3-12 > div.card
  {
    border: none;
  }

  .thumbnail.round-top
  {
    border: 1px solid #000;
    border-radius: 0;
    background-color: white !important;
  }

  .item-content
  {
    font-weight: 500;
    background-color: #E9EBF3;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    border-top: 1px solid #ccc;
    box-shadow: none;
    padding: 10px;
    max-height: 61px;
  }

  div.section:nth-child(1)
  {
    display: inline-block;
    float: left;
  }

  div.section:nth-child(2)
  {
    display: inline-block;
    float: left;
  }

  div.section:nth-child(3)
  {
    float: right;
    margin-top: -16px;
    display: inline-block;
  }

  div.section:nth-child(4)
  {
    float: right;
    display: inline-block;
  }

  .bottom-bar
  {
    margin-top: -2px;
  }

  div.col-10-12.push-1-12 > div.card .content
  {
    font-family: Arial, Helvetica, sans-serif;
    color: #333;
  }

  a#pTrades .nav-notif
