    background-color: transparent;
    padding: 0;
    color: #fff;
    font-size: 17px;
    font-weight: normal;
    border-radius: 0;
  }

  .nav-notif::before
  {
    content: "(";
  }

  .nav-notif::after
  {
    content: ")";
  }

  .register-button
  {
    border: 0;
  }

  .register-button:hover
  {
    background-color: transparent;
    text-decoration: underline;
  }

  .login-button:hover
  {
    background-color: transparent;
    text-decoration: underline;
  }

  .login-button
  {
    border: 0;
  }

  .index-top-bar
  {
    background-color: transparent;
    background-image: url('https://media.discordapp.net/attachments/267810707574226964/424958159199666177/lol.png');
    background-repeat: no-repeat;
    background-position: 50% 20%;
  }

  .index-top-bar img
  {
    visibility: hidden;
  }

  .index-top-bar .bar-1 span
  {
    border: 1px solid #000;
    padding: 4px;
    background-color: #77B9FF;
  }

  .dropdown-content.logout-dropdown li *
  {
    background-color: transparent;
  }

  .arrow-down
  {
    display: none;
  }

  .dropdown-content.logout-dropdown li :hover
