    background-color: transparent;
  }

  .card .thumbnail.dark
  {
    background-color: white;
  }

  nav .push-left
  {
    margin-top: 1px;
  }

  input, textarea
  {
    padding: 2px;
    border-radius: 0;
    background-color: #fff;
    border: 1px solid #888;
    font-family: Arial, Helvetica, sans-serif;
  }

  .alert
  {
    color: #fff;
    text-align: center;
    padding: 3px;
    margin-top: 5px;
    border-radius: unset;
  }

  .alert.success
  {
    background-color: #1fde62;
    border: 1px solid #23a250;
  }

  .alert.warning
  {
    border: 1px solid #b57500;
    background-color: #ffa500;
  }

  .alert.error
  {
    background-color: #ef263d;
    border: 1px solid #8d1e2b;
  }

  .gray-text
  {
    color: #333;
  }

  input::placeholder, textarea::placeholder
  {
    font-weight: unset !important;
    color: #000 !important;
  }

  .input-group
  {
    border: unset;
    border-radius: 0;
    margin-bottom: 0px;
  }

  .input-group.fill input
  {
    overflow: auto;
    border: 1px solid #888;
    border-right: none;
  }

  input.rigid
  {
    overflow: auto;
    border: 1px solid #888;
    height: 30px;
  }

  .input-group .input-button
  {
    color: #fff;
    background-color: #77B9FF;
    padding: 3px;
    font-size: 13.3px;
    font-weight: unset;
    border: 1px solid black;
  }

  .card .top
  {
    box-shadow: unset;
  }

  .profile-card.award img
  {
    border: 1px solid #000 !important;
    background-color: #fff;
    border-radius: unset !important;
  }

  .tabs .tab-body
  {
    border-radius: 0;
  }

  .toggle-user-desc.gray-text
  {
    font-weight: unset;
    color: black;
  }

  .user-description-box
  {
    font-weight: normal;
  }

  a.darker-grey-text.read-more-desc
  {
    background-color: #E9EBF3;
    font-weight: 700;
  }

  .toggle-user-desc:not(.open):after
  {
    background-color: #E9EBF3;
  }

  .select, select
  {
    border-radius: unset;
    font-weight: normal;
    padding: 0px;
    color: #000;
  }

  input#search-bar
  {
    height: 30px !important;
  }

  .block .ellipsis
  {
    color: #1b1b1b !important;
  }
}

