    color: rgb(61, 142, 214);
}
/* Access modifiers */
 .highlight .kd {
    color: rgb(61, 142, 214);
}
/* Methods */
 .highlight .nf {
    color: rgb(220, 220, 170);
}
/* Primitives */
 .highlight .kt {
    color: rgb(61, 142, 214);
}
/* Attributes */
 .highlight .na {
    color: rgb(220, 220, 220);
}
/* Int Numbers */
 .highlight .mi {
    color: rgb(181, 206, 168);
}
/* Double Numbers */
 .highlight .mf {
    color: rgb(181, 206, 168);
}
/* Binary Numbers */
 .highlight .mb {
    color: rgb(181, 206, 168);
}
/* Octal Numbers */
 .highlight .mo {
    color: rgb(181, 206, 168);
}
/* Hexadecimal Numbers */
 .highlight .mh {
    color: rgb(181, 206, 168);
}
/* Strings */
 .highlight .s {
    color: rgb(206, 145, 120);
}
/* // Commands */
 .highlight .c1 {
    color: rgb(87, 166, 74);
}
/* /* Commands */
 .highlight .cm {
    color: rgb(87, 166, 74);
}
/* true/false */
