@-moz-document url-prefix("https://www.haszkod.pl/") {
/* Rozszedzenie widoku */
 :root {
    --main-color: #EA4630;
    --main-dark: #BB2528;

    --input-hover: #FFFFFF;
    --input-dim: #999;
    --a-hover-background: #F8B229;
    --a-hover: #FFFFFF;

    --menu-category: #146B3A;
    --center-background: #FFFFFF;
              
	--text-color: #050505;
    --text-color-contrast: #FFFFFF;
    --text-color-a-hover: #EA4630;

	--list-odd: #165B33;
    --list-even: #146B3A;

    --title-shadow: #008FFF;

    --request-in-progres-title: #008b8b;
    --request-in-progres-details: #004747;
    --request-in-progres-description: #003939;

    --request-done-title: #037a18;
    --request-done-title-link: #FFFFFF;
    --request-done-title-link__hover: #1e9bda;
    --request-done-details: #023e0c;
    --request-done-description: #022808;

    --request-canceled-title: #8f0000;
    --request-canceled-details: #490000;

    --add-haszkod: #1e5938;
}
/* SLIDER */

/* width */
 ::-webkit-scrollbar {
    width: 10px;
}
/* Track */
 ::-webkit-scrollbar-track {
    background: var(--menu-category);
}
/* Handle */
 ::-webkit-scrollbar-thumb {
    background: var(--main-color);
}
/* Handle on hover */
 ::-webkit-scrollbar-thumb:hover {
    background: var(--input-hover);
}

div.supernaglowek_odstep,
div.supernaglowek_odstep2{
    display: none !important;
}

div.supernaglowek {
    width: 1390px;
    background: var(--main-color);
    color: white !important;
    font-size: 12px !important
}

.notifications-info {
    background: var(--main-color) !important;
    border: 1px solid var(--main-dark);
    color: white !important;
}
html>body>table {
    width: 1400px !important;
    max-width: 1400px !important;
}
body {
    background: url("https://i.postimg.cc/W4B0BMbC/Projekt-bez-tytu-u.png");                                            
}
body > table > tbody > tr:nth-child(2), body > table > tbody > tr:nth-child(4), body > table > tbody > tr:nth-child(6), body > table > tbody > tr:nth-child(8) {
