}
/* ------------------------------------ */

/* ------------- PANELIK -------------- */

/* -- Panel wyszukiwania i logowania -- */

/* ------------------------------------ */

/* GÃÂÃÂÃÂÃÂ³wny wyglÃÂÃÂd panelu */
 .panelik {
    height: 40px !important;
    background: var(--main-color) !important;
    border: 0 !important;
/*     border-top: 1px solid var(--main-dark) !important; */
/*     border-bottom: 1px solid var(--main-dark) !important; */
    padding-bottom: 2px !important;
    width: 1400px !important;
    color: white !important;
}
/* Ikona wyszukiwarki */
 .panelik img[alt="szukaj"] {
    width: 20px !important;
    height: 20px !important;
    box-sizing:border-box;
    filter: invert(100%);
    position: relative;
    top: 5px;
}
/* OgÃÂÃÂ³lny selector inputÃÂÃÂ³w */
 .panelik input {
    height: 26px !important;
    background: none;
    border-color: var(--input-hover);
    border-radius: 20px;
    outline: none !important;
    color: white !important;
