    background-size: contain;
}
/* Lista tozwijana - podÃÂÃÂwietlony */
 .panelik select:hover {
    -moz-appearance: none;
    appearance: none;
    -webkit-appearance: none;
    background: transparent url('https://www.materialui.co/materialIcons/hardware/keyboard_arrow_down_white_144x144.png') no-repeat right center;
