/* ------------------------------------ */

/* ---------- PANELE BOCZNE ----------- */

/* ------------------------------------ */

/* UsuniÃÂÃÂcie zbÃÂÃÂdnych spacerÃÂÃÂ³w wewnÃÂÃÂtrz */
 body > table table > tbody > tr >[width='18'], body > table table > tbody > tr >[width='3'] {
