/* New thread button */
a.callToAction span {
    color: rgb(255, 255, 255);
    background: transparent;
}
a.callToAction {
    background: transparent;
    border: none;
    border-radius: 0px !important;
}
a.callToAction:hover {
    border: 1px solid #abff86;
    transition: all 0.8s ease;
}
a.callToAction:hover span {
    background: transparent;
    border: none;
    box-shadow: none;
    color: #fff;
}
a.callToAction {
    display: inline-block;
    text-align: center;
    cursor: pointer;
    text-transform: uppercase;
    outline: none;
    overflow: hidden;
    position: relative;
    color: #fff;
    font-weight: 700;
    font-size: 15px;
    background-color: #2e3539;
    padding: 2px 0px;
    margin: 0 auto;
    box-shadow: 0 2px #2e3539;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, 1);
    border: 1px solid #5d6b73;
    background-size: 10px 10px !important;
    background-image: linear-gradient(45deg, rgba(100, 100, 100, .2) 25%, transparent 25%, transparent 50%, rgba(100, 100, 100, .2) 50%, rgba(100, 100, 100, .2) 75%, transparent 75%, transparent) !important;
    min-height: 100% !important;
}
a.callToAction span {
    position: relative;
    z-index: 1;
}
a.callToAction:after {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    height: 515%;
    width: 125%;
    background: #5dd727;
    border: 1px solid #abff86;
    -webkit-transition: all .5s ease-in-out;
    transition: all .5s ease-in-out;
    -webkit-transform: translateX(-98%) translateY(-25%) rotate(45deg);
    transform: translateX(-98%) translateY(-25%) rotate(45deg);
}
a.callToAction:hover:after {
    -webkit-transform: translateX(-9%) translateY(-25%) rotate(45deg);
    transform: translateX(-9%) translateY(-25%) rotate(45deg);
}



