@-moz-document domain("paste.imirhil.fr"), domain("vim.cx"), domain("priv.atebin.com"), domain("paste.tech-port.de"), domain("paste.jaegers.net"), domain("pastebin.grey.pw") {
/* @import url('https://fonts.googleapis.com/css?family=Nunito&display=swap'); */
body {
    background-color: #f2f2f2;
    /*OUTER background*/
    background-size: 48px 48px;
}
body, h1, h2, h3, h4, h5, h6, pre, p {
    color: #000;
}
h2, h3 {
    border-collapse: separate;
    font-size: 30px;
    font-weight: 500;
    line-height: 33px;
    margin-block-end: 10px;
    margin-block-start: 20px;
    margin-bottom: 10px;
    margin-top: 20px;
    text-align: center;
}
#plaintext {
    float: unset;
    margin: auto;
    background: #fff;
    /*INNER background*/
    font-weight: 400;
    font-size: 22px;
    font-family: 'Nunito', Arial, sans-serif;
    width: 705px;
    line-height: 1.8;
    padding-left: 19px;
    padding-right: 19px;
    padding-top: 19px;
    text-align: justify;
}
.well {
    padding-left: unset;
    padding-right: unset;
    padding-top: unset;
    /*     width: 705px; */
    background-color: #fff;
    /*     outline: 1px solid black; */
    box-shadow: none;
    -webkit-box-shadow: none;
}
.col-md-12 {
    position: unset;
    min-height: unset;
    padding-left: unset;
    padding-right: unset;
}
.col-md-6, .col-md-8 {
    width: 100%
}
.col-md-offset-3 {
    margin: auto;
}
}

