@-moz-document regexp("https://.*hattrick.org\\/([a-z\\-]+\\/)?World\\/Series\\/(default.aspx)?\\?.*") {
.icon-check:before {
    content: "P"    
}

.icon-close:before {
    content: "I"    
}

.icon-minus:before {
    content: "N"    
}

.recent-result {
    border: none;
    border-radius: 2px;
    display: flex;
    height: 18px;
    width: 18px;
    font-size: 11px;
    font-weight: 400;
}

.recent-result-lost {
    background-color: #eb3f30;
}

.recent-result-won {
    background-color: #44ac45;
}

.recent-result-draw {
    background-color: #ffb22f;
}

a.recent-result > i {
    font-family: Tahoma, "Geneva CE", "Lucida Sans", Arial, Helvetica, sans-serif !important;
    font-weight: bold;
    font-size: 11px;
}

.series-table {
    color: #656565;
}

.series-table tr {
    height: 28px;
}

.series-table tr:not(:first-child) {
    border-bottom: 1px solid #e5e5e5;
}

.series-table tbody tr:not(:first-child) > td:nth-child(2), .series-table tbody tr:first-child > th:nth-child(2) {
    display: none;
}

.series-table tbody tr:not(:first-child) > td:first-child {
    padding: 4px 10px;
    background-color: #f3f3f3;
}

.series-table tbody tr:nth-child(2) > td:first-child {
    background-color: #004682;
    color: white;
}

.series-table-team a {
    text-decoration: none;
    color: #656565;
    font-size: 11px;
}

.series-table-logo {
    padding: 5px 3px 5px 7px;
}

.series-table-team a:hover {
    text-decoration: underline;
}

.series-table-team a[href="/Recruitment/"] {
    display: none;
}

.series-table-team a.shy {
    text-decoration: line-through;
}

.series-table-points {
    font-weight: normal;
}

table.series-table th {
    border-bottom: 1px solid #d5d5d5;
    background-color: #e9e9e9;
    font-weight: normal;
    color: #656565;
    vertical-align: middle;
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable {
    font-family: Tahoma, "Geneva CE", "Lucida Sans", Arial, Helvetica, sans-serif;
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable > span:first-child {
    color: #656565;
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable > span.float_right {
    padding: 4px;
    margin: 4px 0;
    display: flex;
    float: none;
    background-color: #085f00;
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable > span.float_right a {
    padding: 6px 8px;
    text-decoration: none;
    color: rgba(255, 255, 255, 0.7);
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable > span.float_right a:hover {
    background-color: #053600;
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable > span.float_right a[disabled] {
    color: white;
    background-color: #053600;
    font-weight: bold;
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable > span.float_right a[disabled]:hover {
    text-decoration: underline;
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable br {
    display: none;
}
}

@-moz-document domain("naruho.do") {
:root {
	--accent: teal;
    --bg-root: black;
    --bg-1: #101010;
    --bg-2: #171717;
    --fg: white;
    --muted: rgba(255, 255, 255, .4);
	--muted-alt: rgba(0, 0, 0, .6);
  	--border: rgba(255, 255, 255, .05);
	
    -ms-overflow-style: -ms-autohiding-scrollbar;
    background-color: var(--bg-root);
    color: var(--fg);
}

body {
	color: inherit;
}

#header {
    background-color: black;
    border-bottom: 6px solid black;
}

div.forum-header,
#wrap_content {
	background: var(--bg-root);
}

#fullnotification,
.tfield, #accounts #import_list input[type="text"] {
	background: var(--bg-1);
	border-color: transparent;
	color: inherit;
}

#shareForm #typeshare {
	background: none;
	border-color: transparent;
}

#shareForm #typeshare div {
	border-top-color: transparent;
}

.comment_wrapper .add-comment, #long_feeds .comments, .box,
.tfield, #accounts #import_list input[type=text],
.comment_wrapper .add-comment textarea,
.more-comments {
    border-radius: .5rem;
}

.comment_wrapper .add-comment,
#long_feeds .comments,
.box {
	background-color: var(--bg-1);
	border-color: transparent;
}

#long_feeds .message img.tail {
	display: none;
}

span.date {
	color: var(--muted);
}

.notificationsList span.date {
	color: var(--muted-alt);
}

.division_line {
	border-color: var(--border);
}

.comment_wrapper .add-comment textarea {
	background-color: var(--bg-2);
	border-color: transparent;
	color: inherit;
}

.more-comments {
	background-color: var(--bg-2);
	border-bottom: none;
}

.comment_wrapper .comments li {
	background: none;
}

#long_feeds .comments li {
	border-color: var(--border);
}

.discussion-post-author,
.discussion {
	color: inherit;
}

blockquote,
.tag-label,
.discussion-stats {
	background-color: var(--bg-1);
}

.in-reply-to,
.discussion-post {
	border-color: var(--border);
}

.discussion-item-title {
	color: var(--accent);
}

#fullnotification .list {
	border-color: var(--border);
}

#fullnotification .ua-notifications>a:hover {
	background-color: var(--bg-2);
}

#fullnotification div.detail p {
	color: var(--fg);
}

#long_feeds .message.box div.center {
	height: auto;
}

.fullimage {
	width: 100%;
	max-height: 75vh;
	object-fit: contain;
}
}
