@-moz-document domain("metal.nightfall.fr") {
.tab6v3 {
    border-style: solid;
    border-color: #9F9F9F;
    border-left-width: 0px;
    border-right-width: 0px;
    border-top-width: 0px;
    border-bottom-width: 0px;

}
table {
    /*background:none;*/
    border-style: solid;
    border-color: #000000!important;
    border-left-width: 0px;
    border-right-width: 0px;
    border-top-width: 0px;
    border-bottom-width: 0px;
}
img[src*="blind.gif"]:after {
   filter: brightness(100);
    display:block;
    content: "Nightfall";
    color: #FFFFFF;
    margin-left:auto;
    margin-right:auto;
    text-align:center;
}
table[background*="bandeau.jpg"] {
    filter: grayscale(1);
}
table[background*="fond.jpg"] {
    background:none;
}
span {
    background-color: #000;
}
body {
    background-image:none!important;
    background-color: #000;
}
}

@-moz-document domain("www.frandroid.com") {
.header .header__border-gradient, .menu-burger::before {
    background: linear-gradient(135deg, #99CF3D 0, #63B84B 60%, #99CF3D 100%)!important;
}
.header .header__top-bar {
    background: linear-gradient(135deg, #99CF3D 0, #99CF3D 60%, #99CF3D 100%)!important;
    /*background: linear-gradient(135deg, #D3D3D3 0, #D3D3D3 60%, #D3D3D3 100%)!important;*/
}
img[src*="frandroid.svg"] {
   filter: brightness(100);
}

.icon, .gtm-menu, .gtm-verb {
   /*filter: invert(60);*/
   filter: brightness(100);
}
.header__category-icon { 
   filter: grayscale(1);
}
.has-text-gradient-blue-inverted,
.has-text-gradient-pink,
.menu-burger__title,
.is-title,
.post-card-edito__gradient-line,
.header__category-link--pink-blue::after,
.header__category-link--orange::after,
.header__category-link--blue-pink::after,
.header__category-link--green::after,
.rainbow-module__title,
.post-card__category,
.post-card__title::after,
.fr-button.fr-button--pink,
.fr-button, 
.fr-button-close {
   filter: invert(60);
}
.menu-burger .menu-burger-background::after, .has-background-gradient-pink {
   filter: grayscale(1);
}
.newsletter-form {
   filter: hue-rotate(270deg);  
   background:none;
}
.post-card--featured, .has-background-gradient-blue {
   background: linear-gradient(-45deg, #99CF3D 0%, #63B84B 100%);
}
.has-background-gradient-pink-inverted {
   background: linear-gradient(-45deg, #63B84B 0%, #99CF3D 100%);    
}
#footer {
   background: linear-gradient(-45deg, #99CF3D 0%, #63B84B 100%);    
}
a h3, .post-card__title-link, .rich-content__item-link, .post-card.post-card--lite:hover {
   color: #000000;
}
.post-card__infos h3 {
   color: #FFFFFF;
}
.rich-content__item-link:hover::before,.rich-content__item-link:link::before {
    background: #000000;
}
.post-card--good-deal .rich-content__item-link:hover
{
   filter: grayscale(1);
}
.post-card__title a:hover {
   filter: invert(60);
}
html {
    background-color: #ececec;
}
}

