@-moz-document domain("blender.stackexchange.com")
{

/* Theme */
:root {
    --content-bg-color: #202020; /* #FFF; */
    --text-color: #b3b3b3; /* #242729; */
    --h1-h2-color: #CCC; /* #242729 */
    --h4-color: #868f97;
    --bg-theme-color: #005587; /* #0571B2; */
    --bg-color: #191919; /* #ECECEC; */
    --bg-polygon: url(https://svgshare.com/i/9ur.svg?v=1e698644746a);
    --bg-pattern: url(https://svgshare.com/i/9v7.svg?v=a22e60b06efd);
    --sprites: url(https://svgshare.com/i/9ug.svg?v=a3cb88dcadcf);
    --border-color: #444;
    --page-border-color: #292929; /* #d6d9dc; */
    --bulletin-bg-color: #2d2b26; /* #FFF8DC */
    --widget-bg-color: #2f2f2f;
    --answered-bg-color: #3f955a;
    --answered-color: #b7ffc0;
    --highlighted-bg-color: #2d2b26;
}
/* General */
body {
    background-color: var(--bg-color) !important;
    background-image: var(--bg-pattern) !important;
    color: var(--text-color) !important;
}
.site-header {
    background-color: var(--bg-theme-color) !important;
    background-image: var(--bg-polygon) !important;
}
.site-footer {
    background-color: var(--bg-theme-color) !important;
    background-image: var(--bg-polygon) !important;
    color: rgba(255,255,255,0.5) !important;
}
#content {
    background-color: var(--content-bg-color) !important;
    border: 1px solid var(--page-border-color) !important;
    border-top-width: 0 !important;
    border-bottom-width: 0 !important;
}
a, .question-hyperlink, .answer-hyperlink {
    color: #3f8cc8;
}
.question-hyperlink:visited, .answer-hyperlink:visited {
    color: #2e6691;
}
.post-text a:not(.post-tag):visited, .comment-copy a:visited, .wmd-preview a:not(.post-tag):visited {
    color: #2e6691;
}
#tabs a, .tabs a {
    color: #bdcbd1;
}
.nav-links .youarehere .nav-links--link {
    background: var(--widget-bg-color);
    color: var(--h4-color);
    border-right: 3px solid  var(--bg-theme-color);
}
.nav-links .nav-links--link:hover {
    color: var(--h4-color);
}
.nav-links .youarehere .nav-links--link:hover {
    color: var(--h1-h2-color);
}
.s-btn__muted.s-btn__outlined {
    border-color: var(--border-color);
}
.s-btn__muted.s-btn__outlined.is-selected {
    border-color: var(--border-color);
    color: var(--text-color) ;
}
.s-btn__muted.s-btn__outlined.is-selected:hover {
    color: var(--h1-h2-color) ;
}
.s-btn__muted.s-btn__outlined:hover {
    color: var(--h4-color);
    border-color: var(--border-color);
}
.bg-black-050 {
    background-color:  var(--widget-bg-color) !important;
}
.excerpt {
    color: var(--text-color);
}
.module.community-bulletin {
    background-color: var(--bulletin-bg-color) !important;
    border: 1px solid var(--border-color);
}
.s-sidebarwidget {
    border: 1px solid var(--border-color);
}
.s-sidebarwidget--header {
    background: var(--widget-bg-color);
    color: #6a737c;
}
.s-sidebarwidget--header, .s-sidebarwidget--content {
    border-top: 1px solid var(--border-color);
}
.s-sidebarwidget:after {
    border: 1px solid var(--border-color);
}
.s-sidebarwidget__yellow {
    background-color: #26251f !important;
}
.s-sidebarwidget__yellow .s-sidebarwidget--header {
    background-color: #312e27 !important;
    color: #dfdac7 !important;
}
.s-sidebarwidget__yellow .s-sidebarwidget--header, .s-sidebarwidget__yellow .s-sidebarwidget--content {
    border-color: #2f2d26 !important;
}
.flush-left {
    border-top: 1px solid var(--border-color) !important;
}
.question-summary {
    border-bottom: 1px solid var(--border-color) !important;
}
.answered-accepted {
    background-color: var(--answered-bg-color);
}
.status.answered-accepted, .status.answered-accepted .mini-counts, .status.answered-accepted .minicounts span {
    color: var(--answered-color);
}
.hi {
    background-color: var(--highlighted-bg-color); 
}
.result-highlight {
    color: #a67216 !important;
}
.user-show-new .answer-summary {
    border-bottom: 1px solid var(--border-color);
}
.user-show-new .user-rep-full .rep-recent-row {
    background-color: var(--highlighted-bg-color);
}
.user-show-new .history-table>tbody>tr {
    border-top: 1px solid var(--border-color) !important;
}
.user-show-new .highlight-row {
    background-color: var(--highlighted-bg-color);
}
.tagged-interesting {
    background-color:  var(--bulletin-bg-color);
}
#tags_list .stats-row, #tags_list .stats-row a, #tags_list .stats-row a:visited {
    color: var(--text-color);
}
#tags_list .tag-cell {
    border-bottom: 1px dotted var(--border-color) !important;
}
input {
    background-color: var(--content-bg-color);
    color: var(--text-color);
}
.newuser {
    background-color: var(--bulletin-bg-color);
    border: 1px solid var(--border-color);
}
.badges-page .general .badge-hierarchy {
    border-top: 1px solid var(--border-color) !important;
}
.review-bar-container .review-bar {
    border: 1px solid var(--border-color);
    background-color: var(--widget-bg-color);
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.8);
}
.review-stats-current-user, .review-stats-count-current-user {
    background-color:  var(--widget-bg-color);
}
.stats-mainbar .task-stat-table td {
    background-color: var(--widget-bg-color) !important;
    border: 5px solid var(--content-bg-color);
}
.help-center .help-category-box h3 {
    border-bottom: 1px solid var(--border-color) !important;
}
.page-numbers {
    border: 1px solid var(--border-color);
}

/* Questions */
.envelope-on, .envelope-off, .vote-up-off, .vote-up-on, .vote-down-off, .vote-down-on, .star-on, .star-off, .feed-icon, .vote-accepted-off, .vote-accepted-on, .vote-accepted-bounty, .badge-earned-check, .delete-tag, .grippie, .expander-arrow-hide, .expander-arrow-show, .expander-arrow-small-hide, .expander-arrow-small-show, .anonymous-gravatar, .badge1, .badge2, .badge3 {
    background-image: var(--sprites),none;
}
#question-header .question-hyperlink {
    color: var(--h1-h2-color) !important;
}
.subheader h1 {
    color: var(--h1-h2-color) !important;
}
.subheader h2 {
    color: var(--h1-h2-color) !important;
}
.subheader {
    border-bottom: 1px solid var(--border-color) !important;
}
#sidebar h4 {
    color: var(--h4-color) !important;
}
.module h4 {
    color: var(--h4-color) !important;
}
.question-status {
    background-color: var(--bulletin-bg-color) !important;
    border: 1px solid rgba(83,90,96,0.1) !important;
}
.answer {
    background-color: var(--content-bg-color) !important;
}
.question {
    background-color: var(--content-bg-color) !important;
}
.show-votes .sidebar-linked .spacer>a:first-child .answer-votes, .show-votes .sidebar-related .spacer>a:first-child .answer-votes {
    background-color:  var(--border-color);
    color: var(--text-color);
}
.show-votes .sidebar-linked .spacer>a:first-child .answer-votes.answered-accepted, .show-votes .sidebar-related .spacer>a:first-child .answer-votes.answered-accepted {
    color: var(--answered-color);
    background-color: var(--answered-bg-color);
}
.label-key b, .label-key strong {
    color: var(--text-color);
    font-weight: normal;
}
#tabs a, .tabs a {
    background-color: var(--content-bg-color);
    color: var(--h4-color);
}
#tabs a:hover, .tabs a:hover {
    background-color: var(--widget-bg-color);
    color: var(--text-color);
}
#tabs a.youarehere, .tabs a.youarehere {
    background-color: var(--content-bg-color);
    border-color: var(--border-color);
    border-bottom-color: transparent;
    color: var(--text-color);
}
#tabs a.youarehere:hover, .tabs a.youarehere:hover{
    background-color: var(--content-bg-color);
    border-color: var(--border-color);
    border-bottom-color: transparent;
    color: var(--h1-h2-color);
}
.f-label, label.f-label {
    color: var(--h4-color);
}
a.bounty-link {
    color: var(--h4-color);
}
kbd {
    color: #000;
    text-shadow: 0 1px 0 #bbb8b8;
    box-shadow: 0 1px 0 rgba(12,13,14,0.2), 0 0 0 2px #c4c0c0 inset;
    background-color: #b5b9bb;
    border: 1px solid #494c4f;
}
pre, code {
    background-color: #303030;
}
.pln {
    color: #d9d6d2;
}
.pun, .kwd, .dec {
    color: #ffe400;
}
.lit, .tag {
    color: #00bdff;
}
.str {
    color: #43c03e;
}
.com {
    color: #8d8885;
}
.typ {
    color: #00bdff;
}
.close-as-duplicate-pane .original-display .list-originals {
    border: 1px solid var(--border-color);
}
.close-as-duplicate-pane .original-display .navi-container {
    color: var(--text-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item .body-summary {
    color: var(--text-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item {
    border-bottom: 1px solid var(--border-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item:hover {
    background-color: var(--widget-bg-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item .stats {
    background: var(--widget-bg-color) !important;
}
.close-as-duplicate-pane .original-display .preview {
    border: 1px solid var(--border-color);
}
blockquote {
    background-color: #3c3726;
    border-left: 2px solid #938339;
}
.question-page #answers .answer {
    border-bottom: 1px solid var(--border-color);
}
.comments {
    border-top: 1px solid  var(--border-color);
}
ul.comments-list .comment>* {
    border-bottom: 1px solid  var(--border-color);
}
.comment-up-off, .comment-flag, .comment-flag-off {
    color: rgba(223, 239, 255, .15);
}
.popup {
    background-color: var(--content-bg-color) !important;
    border: solid 1px var(--border-color) !important;
}
h1 {
    color: var(--h1-h2-color);
}
.deleted-answer {
    background-color: #332b2b !important;
}
.revision td, .owner-revision td, .vote-revision td {
    background-color: #303030;
}
.revision-comment {
    color: #c8d1d9;
}
.diff-choices a {
    border: 1px solid #202020;
}
.diff-choices a.selected {
    border: 1px solid var(--border-color);
    background-color: #303030;
}
span.diff-add {
    color: #D1E1AD;
    background: #2b3c03;
}
span.diff-delete {
    background-color: #6f220d;
    color: #E5BDB2;
}
#revisions .markdown-diff .full-diff .inserted>div {
    background-color: #2a3111;
}
#revisions .markdown-diff .full-diff .deleted>div {
    background-color: #382828;
}
#revisions .markdown-diff .full-diff .skip {
    background-color: #353535;
    border-top: 4px solid var(--content-bg-color);
    border-bottom: 4px solid var(--content-bg-color);
}
#revisions .markdown-diff .full-diff td {
    border-left: 4px solid var(--content-bg-color);
    border-right: 4px solid var(--content-bg-color);
}
.tagged-interesting {
    background-color: #2d2b26;
}
.comment__highlight:not([style*="background-color"]) {
	background-color:#443910;
}
ul.comments-list .comment__highlight .comment-text {
	background-color:#443910;
}
body:not(.no-grid-comments) ul.comments-list .comment__highlight .comment-actions {
	background-color:#443910;
}
.action-list:not(.popup-condensed) .action-selected {
    background-color: var(--highlighted-bg-color) ;
}

/* Input */
textarea, textarea:not(.s-input) {
    color: var(--text-color) !important;
    background-color: var(--content-bg-color);
}
.grippie {
    border: 1px solid var(--border-color);
    border-top-width: 0px;
    background-color: var(--widget-bg-color);
}
.s-input, .s-textarea {
    border: 1px solid var(--border-color);
}
.wmd-button>span {
    background-image: url(https://svgshare.com/i/7uH.svg?v=50d4554b2dd3),none;
}
.wmd-preview {
    border-top: 1px dotted var(--border-color);
    border-bottom: 1px dotted var(--border-color);
}
.wmd-button-row {
    border-bottom: 1px solid var(--border-color);
}
.wmd-button-bar {
    border: 1px solid var(--border-color);
    border-bottom: 0 !important;
}
.wmd-button__active {
    border-left: 1px solid var(--border-color);
    border-right: 1px solid var(--border-color);
    border-bottom: 1px solid var(--content-bg-color);
    background-color: var(--content-bg-color) !important;
}
.s-input[disabled]{
    background-color: #191919 !important;
    color:  var(--border-color) !important;
    border-color: var(--border-color);
}
.wmd-inline-dialog {
    border: 1px solid var(--border-color);
    border-top: 0 !important;
    background-color: var(--content-bg-color) !important;
}
.bt {
    border-top: 1px solid var(--border-color) !important;
}
.s-label {
    color: var(--text-color) !important;
}
.s-btn__danger:hover, .s-btn__danger:focus {
    color: rgb(255, 35, 57) !important;
}
.s-btn:hover, .s-btn:focus {
