@-moz-document domain("malaysia.indeed.com"), domain("messages.indeed.com"), domain("apptracker.indeed.com") {
#app-inner, .icl-NavigableContainer, .appCard, #recentGroup, .icl-NavigableContainer-innerContainer, .msgw-MessagingApp {
    width: 100%;
    background-color: #000 !important;
}
.appCard .icl-NavigableContainer {
    background: #000 !important;
}
body.janus .job-reaction .job-reaction-kebab, body.janus .job-reaction .job-reaction-dislike, body.janus .job-reaction .job-reaction-save, body.janus .job-reaction .job-reaction-unsave {
    background-color: transparent;
}
body.janus .job-reaction .job-reaction-kebab-item:hover {
    background-color: #111;
}
body.janus .job-reaction.is-expanded .job-reaction-kebab-menu {
    position: absolute;
    left: -36px;
    top: 40px;
    padding: 8px 0;
    border: 1px solid #444;
    background: #000;
    box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.08);
    display: flex;
    flex-flow: column;
    min-width: 200px;
    z-index: 3;
    border-radius: 4px;
}
#vjs-jobtitle, .serp-filters-sort-by-container {
    color: white !important;
    transition-duration: 0.8s;
}
.jobCardShelfItem.urgentlyHiring {
    animation-name: spin;
    animation-duration: 2500ms;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
    animation-direction: alternate;
}
@keyframes spin {
    from {
        transform:rotate(3deg);
    }
    to {
        transform:rotate(-3deg);
    }
}
a.myjobs-serp-link {
    animation: blink 0.5s linear infinite;
    animation-direction: alternate;
}
@keyframes blink {
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
    50% {
        opacity: 0.5;
    }
}
html body.ltr.janus, html body.rtl.janus, [dir] .gnav div.gnav-DesktopHeader.is-small-header {
    background-color: #0b3175 !important;
    transition-duration: 0.8s;
}
:root body.janus #jobsearch .sl {
    color: #ff0 !important;
}
:root body.janus table div#vjs-container div#vjs-header {
    background-color: #0b3175;
    color : white !important;
    transition-duration: 0.8s;
}
body.janus table#resultsBody #pageContent #resultsCol .result .salarySnippet .salary .salaryText {
    color:lime !important;
    transition-duration: 0.8s;
}
body.janus #footerWrapper, body.janus #footerWrapper, body.janus #footer {
    border-color: #0b3175 !important;
    background-color: #0b3175 !important;
    color: white !important;
}
