@-moz-document domain(mods.de) {


 
/* Bereiche ausblenden */
img[src*="banner-"],
div.w1,
.copyright
{
display: none !important;
}
 
/* Bilder halbtransparent */
img[src]{opacity:0.05 !important;}
img[src]:hover{opacity:100.0 !important;}
 
/* Miniavatare */
/*
img[src*="avatare"] {
    max-height:40px !important;
    opacity:100.0 !important;
}
*/
 
/*
img[src*="/bb/img/smilies/"],
img[src*="p0t.kicks-ass.net/"],
img[src*="/bb/img/icons/"] {
    max-height:12px !important;
    opacity:100.0 !important;
}
*/
 
/* Werbung für ausblenden */
a[href*="3dsupply.de"] {
    display: none !important;
}
 
/* Kategorien farblich invertieren */
body,div[id^="cat_div"] {
    background-color: #fff !important;
    color: #000 !important;
}
 
/* Quickreply Script */
#qr_row1,
#qr_row2{
    background-color: #fff !important;
}
 
/* New Button Script */
.newInsertButton,
input[type="submit"]{
    background-color: #ccc !important;
    padding: 6px !important;
    border: 0px !important;
    color: #000 !important;
}
 
.spoiler{
    background-color: #fff !important;
    border: 1px solid #ccc !important;
    color: #fff !important;
}
 
span.spoiler{
    color: #000 !important;
}
 
span.marked{
    background-color: #fff !important;
}
 
/*
    In allen <td>,<tr> und <div> Farbe (.*)#07141C(.*) ändern
    (bei Bedarf auch * als Tag-Selektor)
*/
 
tr.b td,
td.b,
div[bgcolor*="#112233"],
div[style*="#07141C"],
td[bgcolor*="#07141C"],
tr[bgcolor*="#07141C"],
.note {
    background-color: #fff !important;
    color:#000;
}
.color2 {
    background-color: #fff !important;
}
.color3x {
    background-color: #fff !important;
}
 
/* PM-System */
tr.hh td, td.hh, tr.h td.hh, td.hx{
    background-color: #fff !important;
    color: #000 !important;
}
 
/* User */
tr.color1 td, td.c vam avatar, td.attrn, td.attrv, tr.color1, #authornote, #authornote a {
    background-color: #fff !important;
    color: #000 !important;
}
 
/*tr.color2 td {
    display: none !important;
}*/
 
div.pp .bordered {
border:0px;
}
 
/* in allen td,tr,div Farbe 2 ändern */
td[bgcolor*="#091827"],
tr[bgcolor*="#091827"],
div[style*="#091827"] {
    background-color: #fff !important;
}
select.quickmod {
    background-color: #fff !important;
}
 
/* in allen td,tr,div Farbe 3 ändern */
tr[bgcolor*="#394E63"],
td[bgcolor*="#394E63"],
div[style*="#394E63"] {
    background-color: #fff !important;
}
 
/* in allen td,tr,div Farbe 4 ändern */
tr[bgcolor*="#BCCACF"],
td[bgcolor*="#BCCACF"],
div[style*="#BCCACF"] {
    background-color: #fff !important;
}
 
/* in allen td,tr,div Farbe 5 ändern */
td.h,tr.h td,
tr[bgcolor*="#222E3A"],
td[bgcolor*="#222E3A"],
div[style*="#222E3A"] {
    background-color: #fff !important;
}
.color3b {
    background-color: #fff !important;
}
 
font[color*="#FFFFFF"] {
    color: #000000 !important;
}
 
/* in allen td,tr,div schwarze Flächen invertieren */
tr[bgcolor*="#000000"],
td[bgcolor*="#000000"],
div[style*="#000000"] {
    background-color: #fff !important;
}
 
/* in allen td,tr,div Farbe 6 ändern */
tr[bgcolor*="#091827"],
td[bgcolor*="#091827"]
,div[style*="#091827"] {
    background-color: #fff !important;
}
.color1 {
    background-color: #fff !important;
}
 
/* in allen td,tr,div Farbe 7 ändern */
tr[bgcolor*="#21354C"],
td[bgcolor*="#21354C"],
div[style*="#21354C"] {
    background-color: #fff !important;
    color: #000 !important;
}
 
/* Farbe 8 ändern */
.color3 {
    background-color: #fff !important;
}
 
/* Farbe 9 ändern */
.color3h {
    background-color: #fff !important;
}
 
/*
    Restliche Graustufen und Fliegendreck
*/
 
