a.bookmark:visited,
a.bookmark nu {
    font-weight: bold;
    color: #5abcfc !important;
}
 
 
 
.ac_entry:hover, .ac_entry_active:hover {
    background-color: #fff !important;
}
 
.ac_entry_active {
    background-color: #fff !important;
}
 
