    color: #704 !important;
}
 
.marked3 {
    color: #540 !important;
    background-color: #779966 !important;
}
 
.markedposttext .posttext {
    color: #100 !important;
}
 
.texthidden {
    color: #fff !important;
}
 
a.bookmark:hover {
    color: #222 !important
}
 
.bookmarklist {
    border: 1px solid #5abcfc !important;
    color: #000 !important;
}
 
 
.bookmarklist hr {
    border: 1px solid #fff !important;
    color: #000 !important;
}
 
 
 
.bookmarklist span {
    color: #000 !important;
}
 
.bookmarklist .bookmark_del_warn {
    color: #066 !important;
}
 
.bookmarklist .bookmark_del {
    color: #0AA !important;
}
 
.newpoll,
a.newpoll:visited {
    color: #A40 !important;
}
 
a.newpoll:hover {
    color: #666 !important;
}
 
a:link {
    color: #000;
}
 
a:visited {
    color: #000;
}
 
a:hover {
    color: #ccc;
}
 
a:active {
    color: #000;
}
 
.linklist a.sel {
color: #000 !important;
}
 
.marked, a.marked {
    background-color: #fff !important;
}
 
a.wht:link,
a.wht:visited {
    color: #000 !important;
}
 
a.catlink:link {
    color: #000 !important;
}
 
a.nu:visited,
a.catlink:visited {
    color: #000 !important;
}
 
a.catlink:active {
    color: #000 !important;
}
 
.option {
    background-color: #fff !important;
    border: 1px solid #fff !important;
}
 
.legende {
    color: #000 !important;
}
 
.on {
    background-color: #fff !important;
}
 
.off {
    background-color: #fff !important;
}
 
input.bordered,
textarea {
    border: 1px solid #ccc !important;
}
 
.ac  {
    background-color: #fed !important;
    border: 1px solid #fff !important;
}
}

