@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("email.seznam.cz") {
  
/* Vyska pisma */    
body, input, textarea, button {
    font-size: 12px !important;
    line-height: 1.2 !important;
    }
    
/* Odsunuty horni okraj leveho menu */    
    nav .content {
    padding-top: 4px !important;
    color: #222;
    }

/* Vyska loga */    
    nav #logo {
    height: 20px !important;
    background-size: 50% auto !important;
    margin-bottom: 4px !important;
    }
    
/* Vyska tlacitka */ 
    button, .button {
       margin-top: 0px !important;
       height: 22px !important;
       padding-top: 4px !important;
       border-radius: 2px !important;
       /* background-color: #D4D0C8 !important; - nelze pouzit, prebarvi vsechna tlacitka */
    }   

/* Vyska radku v menu */    
    nav li a {
    line-height: 22px !important;
    }
/* Vyska radku v comboboxu*/
    div.popup.select > div {
    line-height: 22px !important;
}


/* Horni odsazeni menu od tlacitka */    
    nav ul {
    margin: 8px 0px !important;   
    }    

/* Ikony v menu */    
    .icon {
        height: 14px !important;
        width: 14px !important;
    }        

/* Rozbalovaci menu se seznamem slozek pro presun*/
div.popup.select > div {
    line-height: 20px;
}    
    
/* Seznam zprav */    
/* Pozice zahlavi seznamu zprav*/
    header {
    padding: 6px 24px 0px !important;
    min-height: 68px !important;
    background-color: #FCFAF8 !important;
}    
    
/* Seznam.cz v zahlavi */    
    header > a {
        margin: 4px 0px 0px 12px !important;
}   

/* Prostor okolo tlacitka se jmenem */    
    header #user button {
       padding-top: 2px !important;
    }    
    
/* Stupidni obrazek/fotka u jmena */    
    header #user button .portrait, header #user button .portrait {
       height: 14px !important; 
       width: 14px !important; 
       /* padding-top: 2px !important; -  netreba */
       padding-bottom: 3px !important;
    }
    .portrait {
       border-radius: 0px !important;
    }   
    
    
/* Vyhledavaci radek */    
    form.search input {
       height: 22px !important;
       border-radius: 1px 0px 0px 1px !important;
}
    header > form.search {
       margin-bottom: 10px !important;
}
    
/* Mazaci tlacitko */
    .icon-delete {
        height: 12px !important;
        width: 12px !important;
    }    
    
/* Rolovaci seznam zprav */    
/* Vyska okna seznamu zprav */    
    #list {
       /* Vyska se vypocitava podle min. vysky header (vyse) */        
       height: calc(100% - 68px);
    }

/* Vyska radku v seznamu zprav */    
    li.wide .subject {
       font-size: 12px !important;
       line-height: 1.2 !important;
    }
    .message-list li.wide a {
       padding-top: 4px !important;
       padding-bottom: 4px !important;
}    
    
/* Checkboxy */    
    .message-list li.wide label span {
       top: 5px !important;
    }
    
/* Vyska pisma v odesilateli/adresatovi */
    .message-list li.wide .name, .message-list li.wide .subject {
       font-size: 12px !important;
       line-height: 1.2 !important;
    }

/* Poloha data odeslani/prijeti */    
    .message-list li.wide .misc {
        top: 5px !important;
    }

/* Konec seznamu zprav - cisla stranek pro listovani */    
    .pager {
       margin: 2px 0px !important;
}
    
/* Zapati pod seznamem zprav - vyska textu */    
    #list footer {
        font-size: 12px;
        line-height: 1.2;
