}
.markdown a, .markdown a:visited{
	transition: .2s;
}
.markdown a:hover, .markdown a:visited:hover{
    text-shadow: 0px 0px 2px;
	transition: .2s;
}
.reply{
	line-height: 18px!important;
}
.shadow{
	display: none!important;
}
.status .title:hover, .name:hover {
	text-shadow: 0px 0px 2px;
}
.status .title, .name{
	font-weight: 500!important;
}

/*See everyone that likes your stuff*/
.like-wrap .users {
	overflow-y: scroll!important;
}
.like-wrap ::-webkit-scrollbar {
	width: 0px!important;
	-ms-overflow-style: none;
}
.users {
	background: rgba(0, 0, 0, .2)!important;
	border-radius: 4px 4px 0px 4px!important;
	min-height: 65px;
	min-width: 200px!important;
	right: -12px!important;
	top: -65px!important;
}
a.user {
	float: right;
	border-radius: 4px;
}

/*Dropdown menus are site theme based*/
.el-select-dropdown,
.el-dropdown-menu,
.el-dropdown-menu__item--divided::before {
	background: rgba(var(--color-foreground-blue-dark))!important;
}
.el-select-dropdown__item.hover,
.el-select-dropdown__item:hover {
	background: rgba(var(--color-foreground-grey-light));
}
.el-dropdown-menu__item--divided {
	border-color: rgba(var(--color-background));
}
.el-select-group__wrap:not(:last-of-type)::after {
	background: rgba(var(--color-foreground));
}
.el-popper[x-placement^="bottom"] .popper__arrow,
.el-popper[x-placement^="bottom"] .popper__arrow::after {
	border-bottom-color: transparent;
}
.el-popper[x-placement^="top"] .popper__arrow,
.el-popper[x-placement^="top"] .popper__arrow::after {
	border-top-color: transparent;
}
.el-select-dropdown.is-multiple .el-select-dropdown__item.selected,
.el-select-dropdown.is-multiple .el-select-dropdown__item.selected.hover{
	background: rgba(var(--color-foreground-grey-light));
}

/*Banners don't need shadows*/
.header-wrap .banner {
	margin-top: 0px;
}
.shadow {
	display: none;
}

/*Texteditor*/
.markdown-editor,
.el-textarea__inner {
	border-radius: 0;
}
.markdown-editor {
	margin-bottom: 2px;
}
.markdown-editor > div > svg {
	min-width: 1em!important;
}
.markdown-editor > [title="Image"],
.markdown-editor > [title="Youtube Video"],
.markdown-editor > [title="WebM Video"]  {
	color: rgba(var(--color-red), .6);
}
.markdown-editor > [title="Youtube Video"]:hover {
	color: rgb(255, 0, 0);
}
.activity-entry .details .status:first-letter{
	text-transform: lowercase;
}

/*Donator Badge alignment*/
.details > .donator-badge {
	left: 105px!important;
	padding:3px 5px 3px 5px!important;
	top: 100%!important;
    background: rgba(var(--color-foreground-blue));
	-ms-transform: translate(0px, -34px);
	/* IE 9 */
	-webkit-transform: translate(0px, -34px);
	/* Safari */
	transform: translate(0px, -34px);
	font-size: 1rem!important;
}
.activity-text > div > div > div > .donator-badge {
	position: relative!important;
	display: inline-block!important;
	left: 0px!important;
	top: -10px!important;
	-ms-transform: translate(0px, 0px);
	/* IE 9 */
	-webkit-transform: translate(0px, 0px);
	/* Safari */
	transform: translate(0px, 0px);
}
