@-moz-document domain(forecast.com)
{


/*                             */
/*          MAIN PAGE          */
/*                             */

/* set page background to dark */
body {
    background-color: #2b2a2a;
}

/* make navbar links light */
#navbar nav a {
    color:#bebebe;
}
#navbar nav a:hover {
    color:#d7d7d7;
}

/* flip the pinned navbar colors to dark */
#navbar.pinned {
    background: hsla(0, 0%, 12%, .95);
}

/* make right sticky bar text light */
div.is-sticky, div.preview-event__name {
    color:#bebebe;
}

/* space the name of the trending event out a bit */
div.preview-event__name {
    margin-top:10px;
}

/* hide newsletter & about cards & footer */
div.newsletter, div.about, footer {
    display: none;
}

/* make recent events name light */
div.recent-events {
    color:#bebebe;
}

/* make event name light */
h2.event__name a {
    color: #bebebe;
}

/* make event accent color medium light */
li.event {
    color: #848383;
}

/* make event metadata medium light */
div.event__meta a {
    color: #848383 !important;
}

/* make prev and next links light */
li.page-item a {
    color:#bebebe !important;
}

/* add some room to the bottom of the page */
ul.pagination {
    padding-bottom: 30px;
}

}

